css:
@-moz-document domain("moodle.hamk.fi") {
:root {
	--main-bg-color: #171717;
	--secnd-bg-color: #1b1b1b;
	
	--link-color: #d15f74;
	--link-hover-color: #e96a81;
	
	--text-color: #e3c2b3;
	/*
	--text2-color: #5a4d47;
  background-color: var(--main-bg-color);
	background-color: var(--secnd-bg-color);
	color: var(--link-color);
	color: var(--link-hover-color);
	color: var(--text-color);
	*/
}

/*taustat*/
#region-main *,
.pull-right *{
	background-color: var(--secnd-bg-color) !important;
}

body * {
	/*univ*/
	  background-color: var(--main-bg-color) !important;
	border-color: #1f1f1f !important;
	text-shadow:none !important;
	box-shadow:none !important;
	color: var(--text-color) !important;
}

/*vtn modalit sanon mää */
.modal-backdrop *,
.show{
	background: rgba(27,27,27, 0.6) !important;
}
/*..modaalit*/
/*..taustat*/

/*värit*/
input, select,
.singleselect,
input[type="text"] {
	border-color: var(--text-color) !important; 
}

a,
li * h1, li * h2, li * h3, li * h4, li * h5, li * h6, 
h1,h2,h3,h4,h5,h6{
	color: var(--link-color) !important;
}
.date *, li *{
		color: var(--text-color) !important;
}
a:hover,
span:hover{
	color: var(--link-hover-color) !important;
}

/*..värit*/

/*navit */
.breadcrumb li:last-child *,
.section-navigation *, /*ala navigaatio napit -> <- */
.nav-tabs li:hover *, /*valittu navi */
.nav-tabs li.active *{
	color: var(--link-color) !important;
}
.nav-tabs li:hover *,
.nav-tabs li.active *{
	color: var(--link-color) !important;
}
.nav-tabs li.active *{
		border-color: var(--link-color) !important;
}
.active_tree_node *{
	color: var(--link-color) !important;
}
/*..navi*/

/*kalenteri */
.block .minicalendar td.day.text-center.today *{
	color: var(--link-hover-color) !important;
	border: 1px solid var(--link-hover-color) !important;
}
.calendar_event_course *{
	color: var(--link-color) !important;
}
/*..kalenteri*/
}
