.navbar {
 	background: #fffc00;   
}

.navbar:before {
 	background-image: none;   
}

.page {
 	box-shadow: none;
    border: none;
    border-radius: 0px;
}

div.panel.panel-default:not(.metricsContainer__metricsContainer___2HnXd) {
 	width: calc(100% / 4 - 42px) !important;   
    
