    flex-direction: row;
    justify-content: center;
}

div.panel.panel-default:not(.metricsContainer__metricsContainer___2HnXd) div.panel-body div:first-child {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
}

div[style='position: relative; width: 100%; margin-left: -10px; margin-right: -10px;'] {
 	margin-right: 0px !important;
    margin-left: 0px !important;
}

