css:
@-moz-document regexp("https?://exmo\\.me/../trade\\#\\?pair.*"), regexp("https?://exmo\\.com/../trade\\#\\?pair.*") {
/*** Version 27-September-2018 ***/

/* uncomment next line after installing a style */
/* @import url('https://fonts.googleapis.com/css?family=Exo+2:300,400,500,600,700&subset=cyrillic'); */

/* Font - apply Exo 2 font */
html, body, button, input, optgroup, select, textarea {
    font-family: "Exo 2", sans-serif !important;
}
/* Bold font - more boldness */
.text_bold {
    font-weight: 700 !important;
}

/* Header - shrink height */
.header {
    padding: 5px !important;
    height: 39px !important;
}  
.user_block {
    top: 1px !important;
}

/* Balance drop down - full height, shrink whitespace */
.user_block_balance_drop .table_balance {
    height: auto !important;
}
.user_block_balance_drop table tr td {
    padding: 5px 15px !important;
    height: 10px !important;
}

/* Inner container - move left to allow wider chat on the right */
.section_inner {
    margin-left: 30px !important;
}
.col_1 {
    margin-right: 1% !important;
}

/* Chat - make wider, decrease whitespace, increase font size */
#slider_chat .chat_new,
#slider_chat .chat_new.chat_close {
    left: calc(100% - 500px)!important;
    width: 500px !important;
}
.chat_new_head {
    line-height: 30px !important;
}
.chat_new_msg_unit, .msg_edit {
    padding: 5px 10px 0 !important;
    font-size: 14px !important;
}
#slider_chat .chat_new .chat_new__in .chat_new_footer {
    height: 80px !important;
    padding: 10px 10px !important;
}
#slider_chat .chat_new.chat_close .chat_new_footer {
    height: 0 !important;
    padding: 0 16px !important;
}

/* Auction - shrink whitespace */
.auction_new .auction_new_top {
    margin-top: 10px !important;
    margin-bottom: 0px !important;
}
.auction_new .trade_row {
    margin-bottom: 5px !important;
}
.auction_new .form_title_3 {
    margin-bottom: 19px !important;
    margin-top: 10px !important;
}

/* Graph header - shrink whitespace, more readability */
.auction_new .block_head {
    padding: 2px 20px 2px !important;
}
.block_head {
    font-weight: 600 !important;
    font-size: 13px !important;
}

/* Graph - decrease header height, increase graph height */
.auction_new .auction_new_top .pairs_new_chart {
    height: 518px !important;
}
.auction_new .auction_new_top .graphic_top {
    margin-bottom: 0px !important;
    line-height: 1 !important;
}
div[class^="row chart_body"] {
    height: 490px !important;
}

/* Pairs header - just hide, useless */
.pairs_new_header {
    display: none !important;
}
/* Pairs window - increase height due to hidden header */
.auction_new .auction_new_top .top_settings_table {
    height: 496px !important;
}
.top_pairs_btns {
    height: 26px !important;
}
.auction_new .trade_table .table_header td {
    padding: 5px 0 5px 10px !important;
}
.auction_new .auction_new_top .top_pair_btn {
    padding: 4px 8px !important;
}

/* Trade window input - increase font size, increase font weight */
.trade_row input[type=text]:focus{
    font-size: 16px !important;
    font-weight: 800 !important;
}

/* Cancel orders - hide button, it's annoying on mouse over */
.dropdown_wrap {
    display: none !important;
}

/* Tables - decrease row height */
.trade_table .table_body td {
    padding: 2px 0 1px 10px !important;
    height: 21px; /* don't need !important here */
}

/* Forms, tables, buttons - decrease whitespace */
.form_title, .btn_controls {
    margin-top: 10px !important;
    margin-bottom: 2px !important;
}
.market_order {
    margin-bottom: 0px !important;
}
.amount_body {
    padding: 10px 25px 10px 25px !important;
    height: 265px !important;
}
.table_body_block.orders_active {
    height: 270px !important;
}
.table_body_block.orders_table {
    height: 490px !important;
}
.orders {
    margin-bottom: 25px !important;
}
.btn_buy {
    margin-top: 0px !important;
}
.container {
    padding-bottom: 390px !important;
}

}
