css:
@-moz-document domain("openload.co"), domain("oload.tv"), domain("oload.info") {
#sidebar-wrapper {width: 215px;}
#wrapper {padding-left: 215px;}
.container-fluid {padding-left: 15px; padding-right: 20px;}
#sidebar-wrapper,.sidebar-nav {width: 215px;}
.main-menu .menu-icons {width: 50px;}
#fileman.accountTab {margin-left: -10px;}
.mdl-data-table th {padding: 0 10px 0 10px;}
.main-menu .menu-list {padding-left: 5px; padding-right: 5px;}
#page-content-wrapper {padding-left: 5px;}
.mdl-data-table td:first-of-type, .mdl-data-table th:first-of-type {padding-left: 10px;}
.mdl-data-table td,.material-icons {vertical-align: middle; padding: 0;}
.mdl-data-table tbody tr,.mdl-data-table td {height: 30px;}
.mdl-textfield__input {margin-left: 20px; width: 95% !important;}
.linktextboxrow .mdl-textfield {width: 95% !important;}
#content,.content-holder,.mdl-data-table,.main-menu .menu-list,.content-holder .data-box,#sidebar-wrapper,#page-content-wrapper {background: #353535;}
.hotkeyholder,.content-holder .data-box {box-shadow: 0 0 3px #cccccc;}
.mdl-data-table tbody tr.is-selected,.mdl-data-table tbody tr:hover {background: #757575;}
.jvectormap-container {background: #757575 !important;}
.mdl-data-table td {border-top: 1px solid #aaaaaa; border-bottom: 1px solid #aaaaaa;}
#header,#footer,.mdl-color--white,.content-holder .status-box,.mdl-color--white,.content-holder .info-box,.content-holder .chart-box {background: #505050 !important;}
body,h2,h3,h4,h5,h6,.main-menu .menu-list ul li a,.mdl-data-table th,.mdl-data-table tbody tr, .mdl-data-table td,.mdl-radio,.mdl-textfield__input,.mdl-textfield__label,.content-holder .info-box h2,.content-holder .data-box .data-title h2,#header .top-menu a {color: #cccccc;}
a {color: #209fdf;}
}
