css:
@-moz-document domain("login.emu.dk") {
element.style {}
body {
    font-family: arial, sans-serif;
    font-size: 92.3%;
    background: #000;
    margin: 0;
    padding: 0;
}
outside {
    position: absolute;
    left: 50%;
    top: 50%;
    width: 358px;
    height: auto;
    text-align: left;
    font-size: 0.9em;
    margin: -180px 0 0 -179px;
    border: 3px solid #000000;
    background: #dc9696;
}
#footer {
    clear: both;
    margin-top: 10px;
    text-align: right;
    border-top: 1px solid #000;
    background: #dc9696;
}
#header h1 {
    font-size: 20px;
    color: #000000;
    margin: 0;
    padding: 20px 10px 5px 10px;
    background-color: #dc9696;
    border-bottom: 1px solid #000;
}
input {
    color: #010101
}
.knapperX {
    padding: 0px 2px 0px 2px;
    margin: 2px 2px 1em 2px;
    color: #ffffff;
    background: #dc9696;
    border: 1px solid #000;
    float: right;
    width: auto;
    cursor: pointer;
}
}
