css:
@-moz-document domain("www.turkanime.tv") {
body#bd {
    background: #333;
}
.container, #arkaplan, .panel, nav, .mesajlar, .mesajlar .dropdown-footer, .dropdown-footer, .modal-body{
    background: #222 !important;
    background-color: #222 !important;
    box-shadow: none !important;
}

.dropdown-header, .dropdown-menu
{
 	background: #222 !important;   
}

ul.dropdown-items>li:hover, .dropdown-signout, .dropdown-lockout
{
 	background: #555 !important;   
}

.panel
{
    border: none;
}

.btn-default, .panel-menu, .panel-tabs>li{
    background-color: #666 !important;
    color:  white !important;
    border: none !important;
    background-image: none !important;
}

.panel-tabs>li>a, .panel-tabs>li>a:hover, .panel-tabs>li>a:focus
{
    background-color: transparent !important;
    background-image: none !important;
}

.btn-default:hover, .panel-tabs>li:hover{
    background-color: #222 !important;
}

.btn-default:focus, .panel-tabs>li .active, .panel-tabs>li:focus{
    background-color: #333 !important;
}

.panel-menu{
    background-color: #222 !important;
}


.panel-footer
{
 	background-color: transparent;
	border: none;
}

.panel, .panel-body
{
    background: #333 !important;
    border: none !important;
}

.panel-ust-ic {
    background: #333;
    background-color: #222 !important;
    background-image: none !important;
    border: none;
    color: white !important;
}

body, a, h4, h1, h2, h3, h5, h6, h7, h8, h9
{
  color: white !important;   
}

div, ul, i, p, a, li, .menum li, tr, table,td, input
{
 	border: none !important; 
    border-top: none !important; 
    border-bottom: none !important; 
    border-left: none !important; 
    border-right: none !important; 
    outline: none !important;
    box-shadow: none !important;
}

a.list-group-item {
    background-color: #444 !important;
    border: none;
}

.menum li:hover, .navbar-trans li>a:hover, a.list-group-item:hover
{
    background: none !important; 
  	background-color: #555 !important;
    border: none;
}

.menum li:focus, .navbar-trans li>a:focus, li.active, a.list-group-item:focus
{
    background: none !important;
  	background-color: #232222 !important;
}

.navbar-trans li>a>i
{
 	color: white !important;   
}

.table-striped>tbody>tr:nth-of-type(odd)
{
	background-color: #222;
}


.kul-menu ul.dropdown-items>li:hover i.ikon, .fan ul.dropdown-items>li:hover i.ikon {
    color: #d9534f !important;
}

.colorbegen
{
 	color: lightgreen !important;   
}

.colorbegenme
{
 	color: red !important;   
}

input, textarea {
    border: solid #666 2px !important;
    background-color: #333 !important;
    color: lightgrey !important;
}

.alert-info, .alert-default {
    color: #2e9fff;
    background-color: #07192b;
    
}

.alert-warning {
    color: #969443;
    background-color: #2b2b00;
}

.alert-danger {
    color: #a94442;
    background-color: #2b0201;
}

.oylama>img, .oylama>img:hover, .oylama>img:focus, .oylama>img:active
{
    content: url(https://static1.squarespace.com/static/573f69a67c65e42453c1f54f/t/585725fbebbd1a051da30299/1482106379441/stars_solid+star.png);
    width: 20px;
}

.oylama>img[src*="imajlar/star-on.png"] 
{
	filter: saturate(100);
}

.oylama>img[src*="imajlar/star-off.png"] 
{
	filter: saturate(0);
}
#haberler .thumbnail {
    background-color: transparent;
}

.wysibb, .zamantuneli-mesaj {
    color: black;
}

.close
{
 	color: white !important;
    text-shadow: none !important;
}

.thumbnail .caption
{
 	color: #eee !important;   
}
}
