css:
@-moz-document domain("galaxy.ansible.com") {
.content {
    color: #b4d5f0;
    background-color: #212c31;
}
.table thead {
    background-color: #202020;
}
.ga-table-striped tbody > tr:nth-child(2n+1) {
    background-color: #1b262c;
}
.ga-table-striped tbody > tr:nth-child(2n) {
    background-color: #212c31;
}

#galaxy-role-detail #readme {
    color: #b4d5f0;
}
#galaxy-role-detail #readme h1 {
    color: #809fb9;
    border-bottom: 1px solid #111;
}
.tab-pane{
    margin-top:1.5em;
    border-color: transparent;
    box-shadow: 0 0 10px 0 #121a1e
}

pre {
    color: #b4d5f0;
    background-color: #2e373b;
    border: 1px solid #1b2023;
}

#galaxy-role-detail #readme code {
    color: #b4d5f0;
    background-color: #2e373b;
}
}
