css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.somethingawful.com") {


/*MAIN BACKGROUND - START*/

body,
#container
{
    background-color:#111!important;
    color:#AAA!important;
}

/*MAIN BACKGROUND - END*/

/*NAVIGATION PANES - START*/

#navigation,
.navigation,
#nav_purchase
{
    background-color:#181818!important;
    border-color:#1D1D1D!important;
}

/*NAVIGATION PANES - END*/

/*TABLE COLORS - START*/

table td /*PARENT CELL*/
{
    background-color:#181818!important;
}
table th /*CHILD CELL*/
{
    background-color:#222!important;
}

table,
table td,
table th
{
    border-color:#242424!important;
    border-width:1px!important;
    color:#CCC!important;
}
table th a
{
    background-color:transparent!important;
    color:#DDD!important;
}
#forums a.forum
{
    color:#DDD!important;
}
.subforums a
{
    color:#AAA!important;
}

#forums .forum_21 a.forum, /*FIX FOR COMEDY GOLDMINE AS WHITE FONT IS NOT VISIBLE*/
#forums .forum_21 .subforums a
{
    color:#000!important;
}


/*TABLE COLORS - END*/



/*THREADLIST - START*/

    /*BREADCRUMBS/PAGES - START*/
    .breadcrumbs a,
    .pages a,
    .pages .disabled
    {
        background-color:transparent!important;
        border:none!important;
    }
    /*BREADCRUMBS/PAGES - END*/

    /*SUBFORUMS- START*/
    #subforums tbody tr:first-child td
    {
        border:none!important;
    }
    #subforums
    {
        border:solid 1px #333!important;
    }
    #subforums a
    {
        color:#CCC!important;
    }
    #subforums .subforum dd
    {
        color:#666!important;
    }
    #subforums .subforum .topics,
    #subforums .subforum .posts
    {
        display:none!important;
    }
    /*SUBFORUMS- END*/
    /*FILTER POSTS - START*/
    #filter
    {
        background-color:#181818!important;
        border-color:#1d1d1d!important;
    }
    #filter .toggle_tags
    {
        color:#CCC!important;
    }
    /*FILTER POSTS - END*/

.threadlist a.thread_title
{
    color:#CCC!important;
}

#forum td
{
    background-image:none!important;
    border:solid 1px #242424!important;
}

.lastseen
{
  border-color:#333!important;
  background-color:transparent!important;
  background-image:none!important;
}
.lastseen a.x,
.lastseen a.count
{
  background-color:transparent!important;
  background-image:none!important;
  border-color:#242424!important;
  font-weight:normal!important;
  font-size:10px!important;
  text-decoration:none!important;
}
.lastseen a:hover
{
opacity:0.6!important;
}

.forumbar,
.threadbar
{
background-color:#181818!important;
border-color:#242424!important;
}

/*ARCHIVES*/
#ac_timemachine
{
  background:none!important;
  border:none!important;
  color:#CCC!important;
}

/*THREADLIST - END*/

/*THREAD - START*/
ul.postbuttons
{
background-color:#181818!important;
border:solid 1px #242424!important;
}

table.post tr.seen1 td.userinfo,
table.post tr.seen1 td.postbody,
table.post tr.seen2 td.userinfo,
table.post tr.seen2 td.postbody,
table.post tr.seen2 td.postlinks,
table.post tr.seen2 td.postdate,
table.post tr.altcolor1 td.userinfo,
table.post tr.altcolor1 td.postbody,
table.post tr.altcolor1 td.postlinks,
table.post tr.altcolor1 td.postdate,
table.post tr.altcolor2 td.userinfo,
table.post tr.altcolor2 td.postbody,
table.post tr.altcolor2 td.postlinks,
table.post tr.altcolor2 td.postdate,
table.post tr.seen1 td.postlinks,
table.post tr.seen1 td.postdate,
.privfolder .standard td
{
background-image:none!important;
}

table.post tr.seen1 td.postbody,
table.post tr.seen2 td.postbody
{
opacity:0.6;
}
table.post tr.seen1 td.postbody:hover,
table.post tr.seen2 td.postbody:hover
{
opacity:1;
}

.postbody
{
background-color:#181818!important;
}

#thread tr.seen1 td,
#thread tr.seen2 td
{
background-color:#181818!important;
}
#thread tr.altcolor1 td,
#thread tr.altcolor2 td
{
background-color:#181818!important;
}
.postlinks a,
.postdate a
{
border:none!important;
}
.postlinks a:hover,
.postdate a:hover
{
background-color:inherit!important;
border:none!important;
}

/*USERINFO*/
.userinfo .author
{
color:#CCC!important;
}
.userinfo .op
{
color:#393587!important;
}
tr[id^="pti"] > td:nth-child(1),
.postdate
{
border-right:solid 2px #242424!important;
}
.userinfo .title
{
color:#AAA!important;
}
.postdate
{
color:#AAA!important;
}
.banlink b
{
color:orangered!important;
}

/*Bold/Spoilers/Quotes/etc*/
#thread .postbody b
{
  font-size:110%!important;
}
#thread .postbody .bbc-block
{
  background-color:#202020!important;
  border-color:#242424!important;
}
#thread .postbody .bbc-block blockquote
{
  color:#CCC!important;
}
#thread .postbody .bbc-spoiler
{
  background-color:#444!important;
  color:transparent;
}
#thread .postbody .bbc-spoiler a
{
  color:transparent!important;
}
#thread .postbody .bbc-spoiler:hover a
{
  color:#1462FF!important;
}
#thread .postbody .bbc-spoiler:hover
{
  background-color:#202020!important;
  color:#CCC!important;
}
#thread .postbody pre,
#thread .postbody pre code
{
  color:#CCC!important;
}
.postbody .editedby span
{
color:#AAA!important;
}
/*yospos spoiler colour*/
.forum_219 span.bbc-spoiler
{
  color:transparent!important;
}



/*ICON*/
#thread dl.userinfo dt[class="author platinum"],
#thread dl.userinfo dt[class="author platinum "]
{
  background-image: url("http://forums.somethingawful.com/favicon.ico")!important;
  background-size:14px!important;
}

/*USERCP*/
#usercpnav
{
  background-color:#222!important;
  border-color:#242424!important;
}
.standard h2,
#buddylist
{
background-color:#181818!important;
border:solid 1px #242424!important;
}
.standard h2 a
{
background-color:#181818!important;
}
#cpmain .inner
{
background-color:#222!important;
border:solid 1px #242424!important;
}
.standard
{
background-color:inherit!important;
}
.privfolder .standard td
{
border-color:#242424!important;
}


/*------------------------------------------------------------------------------------------*/
/*GLOBAL LINK COLOR*/
a
{
    color:#1462C1!important;
}
a:hover
{
    text-decoration:underline!important;
}


/*THINGS I DON'T LIKE - START*/

/*UNDERLINE LINKS*/
a:not(:hover)
{
    text-decoration:none!important;
}

/*BOX SHADOWS*/
*
{
    box-shadow:none!important;
}
/*TEXT SHADOWS*/
*
{
  text-shadow:none!important;
}

/*THINGS I DON'T LIKE - END*/

}
