css:
@-moz-document domain("members.profitscraper.com") {
input[type=checkbox] {
 zoom: 2.5;
 margin: 0;
 padding: 0;
}
.btn {
border-radius:3px!important;
}
input[type=checkbox]:hover {
 border: solid;
 cursor: pointer;
 outline: -webkit-focus-ring-color auto 5px;
 outline-color: -webkit-focus-ring-color;
 outline-style: auto;
 outline-width: 15px;
 outline-offset: -2px;
}
ul.dropdown-menu i.fa {

 border-radius: 3px;
 margin-right: 7px;
 padding: 1px;
}
ul.dropdown-menu i.fa.fa-cloud-upload {
 background-color: #1bbc9a;
  color: white!important;
}
ul.dropdown-menu i.fa.fa-thumbs-o-up {
 background-color: #3697db;
 color: white!important;
}
ul.dropdown-menu i.fa.fa-minus {
 background-color: #e7515a;
 color: white!important;
}
ul.dropdown-menu .fa {
 zoom: 1.4!important;
}
.container-fluid {
 padding-left: 15px;
 padding-right: 15px;
}
.table .btn {
 margin-bottom: 3px;
 border-radius: 3px!important;
}
.btn.btn-outline.blue {
 background-color: #3598dc;
 color: white;
}
.btn.btn-outline.red {
 background-color: #e7505a;
 color: white;
}
.btn.btn-outline.green-meadow {
 background-color: #1BBC9B;
 color: #FFF;
}
.btn:hover {
 transition: all 0s;
 color: #0c0c0c!important;
}
@media only screen and (max-width: 600px) {
 .container, .container-fluid {
  padding-left: 0px;
  padding-right: 0px;
  margin: 0px;
 }
 .portlet-body {
  padding-left: 0px!important;
  padding-right: 0px!important;
 }
 .page-container-bg-solid .m-heading-1.m-bordered {
  border: none;
 }
 .page-head page-toolbar {
  text-align: center;
 }
 .page-head .page-title {
  display: inline-block;
  float: none;
  padding: 0px;
  text-align: center;
  width: 100%;
 }
 .page-footer div {
  text-align: center;
 }
 .page-toolbar btn-group {
  display: inline-grid;
  border: solid
 }
 .page-toolbar .btn {
  bordeR: solid 3px pink!important;
  text-align: center!important;
  display: inline-grid;
 }
}
.table-scrollable {
 font-size: 1.4rem;
 font-family: sans-serif!important;
}
.table-striped>tbody>tr:nth-of-type(odd) {
 background-color: #f4f5f7;
}
i.fa.fa-file-text {
 zoom: 2.5;
}
.table-checkable tr>td:first-child,
.table-checkable tr>th:first-child {
 min-width: 10px;
}
}
