.PageNav a.currentPage {
    color: rgb(255,252,252);
    background-color: rgb(188,165,217);
    border-color: rgb(188,165,217);
    position: relative;
}
.nodeList .categoryStrip .nodeTitle a {
  	color: rgb(243,243,243);
   font-weight:bold;
    font-size: 24px;
  
}


body .itemPageNav a {
    color: rgb(255,252,252);
    background-color: #735c71;
    padding-bottom: 0px;
    padding: 1px 10px;
    font-weight: bold;
    margin-right: 3px;
    border: 0px solid rgb(0, 0, 0);
    border-radius: 2px;
}

body .itemPageNav a:hover {
    color: rgb(255,252,252)!important;
    text-decoration: none;
    background-color: rgb(188,165,217);
    border-color: rgb(188,165,217);
}

.node .tinyIcon {
    color: #735c71;
    background-color: rgba(255, 235, 59, 0);
    margin: -4px 9px 0px 9px;
    /* padding-bottom: 3px; */
    display: block;
    white-space: nowrap;
    overflow: hidden;
    opacity: 1;
}


/* footer */

.footerLinks .fa {
color: #e1e6ee!important;
}

.sectionFooter {
    overflow: hidden;
    zoom: 1;
 
    font-size: 13px;
    color: #e1e6ee !important;
    background-color: #343d46;
    background-repeat: repeat-x;
    background-position: top;
    padding: 4px 10px;
    line-height: 16px;
    /* background-image: url(styles/flat/xenforo/gradients/category-23px-light.png); */
}


.sidebar {
    float: right;
    font-size: 13px;
    background-color: #343d46;
    width: 100%;
}

.footer .pageContent {
    font-size: 13px;

    background-color:  #343d46!important;
    padding: 5px;
    overflow: hidden;
    zoom: 1;
}
.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    font-size: 13px;
    color: #e1e6ee !important;
    background-color: rgba(52, 61, 70, 0);
    padding: 0;
    font-weight: bold;
    margin: 4px 0 9px;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}
.footerLegal .pageContent {
    font-size: 11px;
    overflow: hidden;
    zoom: 1;
    padding: 5px 5px 15px;
    text-align: center;
    display:none;
}
.footer a, .footer a:visited {
    color: #e1e6ee;
    text-decoration: none;
    padding: 5px;
    display: block;
}
}
