css:
@-moz-document domain(dkpittsburghsports.com) {

	body.custom-background{

		background-color: #2d2d2d !important
	}

	h1.post-title, #content-main h1.post-title {
		color:white;
	}

	article#post-area {
		background-color: #2d2d2d !important
	}

	#content-main p{
		color:white;
	}

	#post-header{
		background-color: #2d2d2d !important
	}

	.now-playing-sidebar{
		color:white;
		background-color: #2d2d2d !important
	}

	.side-widget{
		background-color: #2d2d2d !important
	}

	.wp-caption, #content-main p.wp-caption-text, .gallery-caption{
		color:#067ed4;
	}

	span.post-header{
		color:white;
	}

	.mvp-related-text a{
		color: white;
	}

	#home-mid-wrap{
		background-color: #2d2d2d !important
	}

	.blog-widget-text h2{
		color:white;
	}

	.postdate{
		color:#067ed4;
	}

	h3.side-list-title, h3.side-list-title a{
		color:white;
	}

	.blog-widget-text p, .feat-widget-text p{
		color:white;
	}

	.feat-widget-text{
		background-color: #2d2d2d !important;
		color: white;
	}

	.feat-widget-wrap{
		background-color: #2d2d2d !important;
	}

	.feat-widget-text h2{
		color:white;
	}

	.byline{
		color: #b9b46e;
	}

	.location {
		color: #b9b46e;
	}

	span.side-list-cat{
		color: white;
	}

	.row-widget-text p{
		color:white;
	}

	.archive-list-text p{
		color:white;
	}

	.archive-list-text h2{
		color:white;
	}


	h1.arch-head{
		color:white;
	}

	#wpcomm .wc-comment .wc-comment-right{
		background: #4a4a4a;
	}

	#wpcomm .wc-reply .wc-comment-right{
		background: #4a4a4a;
	}
}
