}
.rarity-6 .itemname {
    background: linear-gradient(to right, #15242c 0%, #c59a9680 100%);
}
.itemtip.rarity-6 .itemname .itemtitle {
    color: #ffd0cc;
}
.rarity-5 .itemname {
    background: linear-gradient(to right, #15242c 0%, #dab58780 100%);
}
.itemtip.rarity-5 .itemname .itemtitle {
    color: #ffe7c4;
}
.rarity-4 .itemname {
    background: linear-gradient(to right, #15242c 0%, #8f8acd80 100%);
}
.itemtip.rarity-4 .itemname .itemtitle {
