#itemnomove li.itemnomove, form#changedragon fieldset #window #itemlist #a1, form#changedragon fieldset #window #itemlist #a2, form#changedragon fieldset #window #itemlist #a3, form#changedragon fieldset #window #itemlist #a4, form#changedragon fieldset #window #itemlist #a5, form#changedragon fieldset #window #itemlist #a6, form#changedragon fieldset #window #itemlist #a7, form#changedragon fieldset #window #itemlist #a8, form#changedragon fieldset #window #itemlist #a9, form#changedragon fieldset #window #itemlist #a10, form#changedragon fieldset #window #itemlist #a11, form#changedragon fieldset #window #itemlist #a12, form#changedragon fieldset #window #itemlist #a13, form#changedragon fieldset #window #itemlist #a14, form#changedragon fieldset #window #itemlist #a15, form#changedragon fieldset #window #itemlist #a16, form#changedragon fieldset #window #itemlist #a17, form#changedragon fieldset #window #itemlist #a18, form#changedragon fieldset #window #itemlist #a19, form#changedragon fieldset #window #itemlist #a20 {
