css:
@-moz-document regexp("^\\S+/swagger-ui.*$"), regexp("^\\S+/docs.*$") {
body {
    background: #303030;
}
input {
    background: #303030;
    color: #ddd;
    border: none;
}
.swagger-section #header {
    background-color: #202020;
}
.swagger-section #message-bar {
    color: #ddd;
}
.info_title {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource div.heading h2 a:hover {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap p {
    color: #ccc;
}
.swagger-section .swagger-ui-wrap .model-signature .propType {
    color: #606dca;
}
.swagger-section .swagger-ui-wrap .model-signature .signature-nav a:hover {
    color: #ddd;
}
/*POST Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.post div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.post div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.post div.heading h3 span.http_method a {
    background-color: rgba(84, 127, 0, 0.79);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.post div.content h4 {
    color: rgba(84, 127, 0, 0.79);
}
.swagger-section .swagger-ui-wrap .code,
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation div.heading h3 span.path a {
    color: #ddd;
}
/*PUT Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.heading h3 span.http_method a {
    background-color: rgba(230, 156, 50, 0.75);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.content h4 {
    color: rgba(230, 156, 50, 0.75);
}

/*PUT Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.heading h3 span.http_method a {
    background-color: rgba(230, 156, 50, 0.75);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.put div.content h4 {
    color: rgba(230, 156, 50, 0.75);
}

/*PATCH Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.patch div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.patch div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.patch div.heading h3 span.http_method a {
    background-color: rgba(230, 156, 50, 0.75);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.patch div.content h4 {
    color: rgba(230, 156, 50, 0.75);
}

/*GET Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.get div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.get div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.get div.heading h3 span.http_method a {
    background-color: rgba(83, 102, 241, 0.45);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.get div.content h4 {
    color: rgba(83, 102, 241, 0.45);
}
/*DELETE Colors*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.delete div.heading {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.delete div.content {
    background: #202020;
    border: none;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.delete div.heading h3 span.http_method a {
    background-color: rgba(204, 33, 33, 0.75);
}
.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation.delete div.content h4 {
    color: rgba(204, 33, 33, 0.75);
}
/*------------------------------*/

.swagger-section .swagger-ui-wrap ul#resources li.resource ul.endpoints li.endpoint ul.operations li.operation div.heading h3 span.http_method a {
    border-radius: 0;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource:hover div.heading h2 a,
.swagger-section .swagger-ui-wrap ul#resources li.resource.active div.heading h2 a {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap ul#resources li.resource:hover div.heading ul.options li a,
.swagger-section .swagger-ui-wrap ul#resources li.resource.active div.heading ul.options li a {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap .model-signature pre:hover {
    background-color: #303030;
}
.swagger-section .swagger-ui-wrap pre {
    background-color: #303030;
    color: #ddd;
    border: none;
}
.swagger-section pre code,
.swagger-section pre .subst,
.swagger-section pre .tag .title,
.swagger-section pre .lisp .title,
.swagger-section pre .clojure .built_in,
.swagger-section pre .nginx .title {
    color: #ddd;
}
.swagger-section pre .string,
.swagger-section pre .title,
.swagger-section pre .constant,
.swagger-section pre .parent,
.swagger-section pre .tag .value,
.swagger-section pre .rules .value,
.swagger-section pre .rules .value .number,
.swagger-section pre .preprocessor,
.swagger-section pre .ruby .symbol,
.swagger-section pre .ruby .symbol .string,
.swagger-section pre .aggregate,
.swagger-section pre .template_tag,
.swagger-section pre .django .variable,
.swagger-section pre .smalltalk .class,
.swagger-section pre .addition,
.swagger-section pre .flow,
.swagger-section pre .stream,
.swagger-section pre .bash .variable,
.swagger-section pre .apache .tag,
.swagger-section pre .apache .cbracket,
.swagger-section pre .tex .command,
.swagger-section pre .tex .special,
.swagger-section pre .erlang_repl .function_or_atom,
.swagger-section pre .markdown .header {
    color: #ff5f5f;
}
.swagger-section .swagger-ui-wrap input.parameter {
    border: none;
}
.swagger-section .swagger-ui-wrap table tbody tr td {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap .model-signature .signature-nav .selected {
    color: #ddd;
}
.response-content-type {
    color: #ddd;
}
.swagger-section .swagger-ui-wrap .body-textarea {
    color: #ddd;
    background: #303030;
    border: none;
}
select {
    background: #303030;
    border: none;
    color: #ddd;
}
.swagger-section #header form#api_selector .input a#explore {
    border-radius: none !important;
    -webkit-border-radius: none !important;
    -moz-border-radius: none !important;
    padding: 4px 10px !important;
}
::-webkit-scrollbar {
    width: 8px;
}
::-webkit-scrollbar-track {
    background: transparent;
}
::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, .1);
    border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
    background: rgba(255, 255, 255, .25);
}
::-webkit-scrollbar-thumb:active {
    background: rgba(255, 255, 255, .5);
}
.swagger-section .swagger-ui-wrap .model-signature .description .strong {
    color: #bfbfbf;
}
.swagger-section .swagger-ui-wrap .model-signature .propOpt {
    color: #ccc;
}

.swagger-section .snippet_json code.hljs.json {
 	color: rgba(230, 156, 50, 0.75);  
}

.swagger-section .hljs-string,.hljs-number {
 	color: #96b8d4!important;
}

.swagger-section .swagger-ui-wrap table thead tr th{
 	color: #aaa;   
}
}
