css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("cc.akliz.net") {

/*#####################################
          Global STYLES
#####################################*/	
body {
    background-color: #36393E;
  	max-width: 100%;
	  max-height: 5500px;
	  width: 100%;
	  height: 100%;
}	
html {
	margin: 0;
	padding: 0;
	border: 0;
	height: 80%;
}	
#middle {
	max-width: 100%;
	max-height: 100%;
	
	height: 100%;
	color: #fff;
	background: #36393E;
	border: 0px;
	border-top: none;
}
	
.container {
	max-width: 100%;
	max-height: 100%;
	
	width: 100%;
	height: 100%;
	margin: 0 auto;
}
/*#####################################
          Navigation Bars
#####################################*/
#nav {
	max-width: 100%;
	max-height: 100%;
	background: #272B35;
	height: 30px;
	border-bottom: 1px solid #8FA1B2;
}
#server-details {
	max-width: 100%;
	max-height: 100%;
	border-top: 1px solid #8FA1B2;
	background: #181D20;
	padding: 5px;
	line-height: 29px;
}	
.header {
	max-width: 100%;
	max-height: 100%;
	background: #272B35;
	height: 30px;
	line-height: 30px;
	padding: 15px 20px;
}	
	
	
	
/*#####################################
          SideBar Area 
#####################################*/
.span-4 {
	max-width: 100%;
	max-height: 100%;
	width: 10%;
	height: 100%;
	background-color: #313439;
	border-right: 1px solid #8FA1B2;
	margin-right: 0px;
}
.span-19 {
	max-width: 100%;
	max-height: 100%;
  width: 85.7% !important;
	height: calc(100% - 50px);
  overflow: auto;
}		
	
/*#####################################
          Console Area 
#####################################*/	
.tabs {
	max-width: 100%;
	max-height: 100%;
	height: 29px;
	margin-bottom: 10px;
	border-bottom: 1px solid #8FA1B2;
	padding: 0 0px;
	width: 100% !important;
}	
#embedded-console {
	max-width: 100%;
	max-height: 100%;
	background: #000;
	color: #DDD;
	width: 97%;
	padding: 10px;
	overflow: scroll;
	white-space: nowrap;
}	
	
	
/*#####################################
          Footer Area 
#####################################*/
#footer {
  text-align: center;
	background-color: #2A2C31;
	color: #fff;
	height: 30px;
	padding: 10px;
  position:fixed;
  bottom:-25px;
	width: 100%;
}




















}
