css:
@-moz-document url-prefix("http://www.jianshu.com"), url-prefix("https://www.jianshu.com") {
/*
拓宽简书的文章阅读界面,更好利用宽屏.
*/
._gp-ck{
    width: 100%
}

._3Pnjry{
    display: none;
}
/*以下为代码块配色,这里使用的是Xcode配色,如不喜欢请删除以下代码 已经作废*/
.hljs {
    display: block;
    overflow-x: auto;
    padding: 0.5em;
    background: #fff;
    color: black;
}
.hljs,pre {
    background: #f6f6f6;
    color: #657b83;
    -webkit-text-size-adjust: none;
    -ms-text-size-adjust:none;
}
.diff .hljs-header,
.hljs-comment,
.hljs-doctype,
.hljs-pi,
.lisp .hljs-string {
    color: #006a00;
}
.css .hljs-tag,
.hljs-addition,
.hljs-keyword,
.hljs-request,
.hljs-status,
.hljs-winutils,
.method,
.nginx .hljs-title {
    color: #aa0d91;
}
.hljs-meta {
    color: #aa0d91;
}
.hljs-command,
.hljs-doctag,
.hljs-hexcolor,
.hljs-link_url,
.hljs-number,
.hljs-regexp,
.hljs-rule .hljs-value,
.hljs-string,
.hljs-tag .hljs-value,
.tex .hljs-formula {
    color: #c41a16;
}
.css .hljs-function,
.hljs-built_in,
.hljs-chunk,
.hljs-decorator,
.hljs-id,
.hljs-identifier,
.hljs-localvars,
.hljs-name,
.hljs-title,
.vhdl .hljs-literal {
    color: #008;
}
.hljs-attribute,
.hljs-class .hljs-title,
.hljs-constant,
.hljs-link_reference,
.hljs-parent,
.hljs-type,
.hljs-variable,
.lisp .hljs-body,
.smalltalk .hljs-number {
    color: #9b703f;
}
.css .hljs-pseudo,
.diff .hljs-change,
.hljs-attr_selector,
.hljs-cdata,
.hljs-header,
.hljs-pragma,
.hljs-preprocessor,
.hljs-preprocessor .hljs-keyword,
.hljs-shebang,
.hljs-special,
.hljs-subst,
.hljs-symbol,
.hljs-symbol .hljs-string {
    color: #cb4b16
}
.hljs-deletion,
.hljs-important {
    color: #0000ff;
}
.hljs-link_label {
    color: #6c71c4
}
.tex .hljs-formula {
    background-color: #eee;
    font-style: italic;
    background: #eee8d5
}
}
