/* CSS Document */
/*@name        Stylish for Javlibrary
  @namespace   http://www.javlibrary.com
  @description Stylish for Javlibrary
  @author      Sotary
  @version     v1.10
  @version     v2017-11-15
  @reference   
*/

@-moz-document domain("javlibrary.com") {
/******************BLOCK ADS******************/
	br,
	div#topbanner11,
	div#bottombanner11,
	div#bottombanner22,
	div#bottommenu,
	div#middlebanner11,
	div#idsearchboxmask,
	div[style="display:block; position: relative; text-align: right; vertical-align: top; margin-bottom: 5px; height:60px;"],
	div[style="display:block; text-align: right; border-bottom: dotted 1px #cccccc; margin-top: 10px;"],
	div.menul1 + table,
	div.titlebox,
	table.about,
	td.socialmedia,
	div.socialmedia,
	div.previewthumbs,
	span.icn_favdirector,
	span.icn_favmaker,
	span.icn_favlabel,
	span.icn_favstar,
	span.icn_favuser,
	div#video_review.item,
	div.toolbar,
	div.nickname,
	#video_reviews td.scores,
	#video_comments td.scores,
	hr.grey,
	a.icn_want,
	a.icn_seen,
	a.icn_have,
	div#accordion > h3,
	div#bottomcopyright > div,
	iframe {
		display: none !important;
	}
	

	body.main {
		position: relative !important;
		font-family: "MuseoSnsW00-Cond300" !important;
		width: 100% !important;
		height: 100% !important;
		padding: 0 !important;
		margin: 0 !important;
		background: rgba(255,255,255,1) !important;
	}
	div,span,p,table,td,th,h1,h2,h3,h4,h5,h6 {
		color: rgba(0,0,0,0.75) !important;
	}
	a {
		font-family: "MuseoSnsW00-Cond500" !important;
		color: rgba(0,128,255,1) !important;
	}
	a:hover {
		color: rgba(0,180,255,1);
	}
	
	/******************TOPMENU******************/
	div#topmenu {
		z-index: 999 !important;
		position: fixed !important;
		top: 0;
		left: 0;
		width: inherit !important;
		height: 55px !important;
		margin: 0 auto !important;
		padding: 10px 0 !important;
		background: rgba(255,255,255,1) !important;
		border-bottom: 1px solid rgba(0,128,255,0.1) !important;
	}
	div#toplogo {
		height: 55px !important;
	}
	div#topmenu .searchbar,
	div#topmenu .menutext {
		position: relative !important;
		left: 0 !important;
		display: inline-block !important;
		margin: inherit !important;
		padding: 3px 10px !important;
		font-family: "MuseoSnsW00-Cond500" !important;
		font-size: 1.01em !important;
		vertical-align: middle !important;
		text-transform: uppercase !important;
		color: rgba(255,255,255,1) !important;
	}
	div#topmenu .searchbar table {
		border-spacing: 0 !important;
	}
	div#topmenu .searchbar td.advsearch {
		width: auto !important;
		vertical-align: middle !important;
		font-family: inherit !important;
		font-size: inherit !important;
		color: inherit !important;
	}
	div#topmenu .searchbar td input#idsearchbox {
		font-family: inherit !important;
		padding: inherit !important;
		margin: inherit !important;
		border: 1px solid rgba(0,128,255,0.75) !important;
		border-radius: 2px !important;
		width: 320px !important;
		min-height: 42px !important;
		max-height: 42px !important;
		background: none !important;
		font-size: 1.45em !important;
		font-weight: bold !important;
		color: rgba(0,128,255,1) !important;
	}
	div#topmenu .searchbar td input#idsearchbox:hover,
	div#topmenu .searchbar td input#idsearchbox:focus {
		background: rgba(255,255,255,1) !important;
		transition: all 0.3s !important;
	}
	div#topmenu .searchbar td input#idsearchbutton,
	div#topmenu .searchbar td.advsearch,
	div#topmenu .menutext a {
		position: unset !important;
		display: inline-block !important;
		font-family: inherit !important;
		font-size: inherit !important;
		font-weight: normal !important;
		color: rgba(255,255,255,1) !important;
		padding: 0 10px !important;
		margin: 0 !important;
		width: auto !important;
		min-height: 46px !important;
		line-height: 46px !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: none !important;
		background: rgba(0,128,255,0.95) !important;
		cursor: pointer !important;
		text-align: center !important;
	}
	div#topmenu .searchbar td input#idsearchbutton:hover,
	div#topmenu .searchbar td.advsearch:hover,
	div#topmenu .menutext a:hover {
	  background: rgba(0,180,255,1) !important;
		color: rgba(255,255,255,1) !important;
		transition: all 0.3s;
	}
	
	
	div#topmenu .searchbar td.advsearch {
		margin: 1px 5px !important;
		text-align: center !important;
	}
	
	div#topmenu .searchbar td.advsearch a {
		color: rgba(255,255,255,1) !important;
	}
	div#topmenu .searchbar td.advsearch a:hover {
		color: rgba(255,255,255,1) !important;
		font-weight: normal !important;
		transition: all 0.3s;
	}
	
	
	div#toplogo .sitelogo {
		z-index: 1000 !important;
		display: block !important;
		position: fixed !important;
		left: unset !important;
		right: 0 !important;
		top: 0 !important;
		margin: 12px 100px 12px 0 !important;
	}
	
	div.languagemenu {
		z-index: 1000 !important;
		display: block !important;
		position: fixed !important;
		right: 0 !important;
		top: 0 !important;
		vertical-align: middle !important;
		width: 180px !important;
		min-height: 46px !important;
		max-height: 46px !important;
		line-height: 45px !important;
		padding: 0 5px !important;
		margin: 14px 0 12px 5px !important;
		text-align: right !important;
		color: transparent !important;
	}
	div.languagemenu > select {
		width: auto !important;
		height: 46px !important;
		background: rgba(0,128,255,0.95) !important;
		border: 1px solid rgba(0,128,255,0.95) !important;
		border-radius: 2px !important;
		color: rgba(255,255,255,1) !important;
	}
	div.languagemenu > select > option {
		border-bottom: 1px solid rgba(0,0,0,0.15) !important;
		padding: 10px 2px !important;
		text-align: left !important;
		vertical-align: middle !important;
		
	}
	
	/*===================================CONTENT CONTAINER=========================================*/
	div#content {
		display: block !important;
		width: 100% !important;
		min-height: 518px !important;
		margin: 0 auto !important;
		padding: 40px 0 115px 0 !important;
		overflow: hidden !important;
		background: rgba(240,240,240,1) !important;
		box-shadow: 0.5px 0.5px 5px rgba(0,0,0,0.25) !important;
		text-align: unset !important;
	}
	
	
	/*===================================CONTENT-LEFTMENU=========================================*/
	div#content #leftmenu {
		display: block !important;
		float: left !important;
		width: 240px !important;
		margin: 0 0 0 10px !important;
		text-align: unset !important;
	}
	#leftmenu .menul1 {
		padding: 0 !important;
		margin: 0 !important;
		font-family: "MuseoSnsW00-Cond300" !important;
		font-size: 1.01em !important;
		color: rgba(255,255,255,1) !important;
		background: inherit !important;
	}
	#leftmenu .category {
		height: 38px !important;
		line-height: 38px !important;
		padding: 0 0 0 5px !important;
		font-weight: bold !important;
		background: rgba(255,255,255,1) !important;
		border-bottom: 1px solid rgba(0,0,0,0.05) !important;
		text-transform:  uppercase !important;
	}
	#leftmenu li {
		border: none !important;
	}
	#leftmenu li a {
		display: block !important;
		padding: 0 0 0 15px !important;
		margin: 0 !important;
		height: 32px !important;
		line-height: 32px !important;
		background: rgba(255,255,255,1) !important;
		border-bottom: 1px solid rgba(0,0,0,0.05) !important;
		border-left: none !important;
		border-right: none !important;
	}
	#leftmenu li a:hover {
		transition: all 0.3s !important;
		font-weight: bold !important;
		border-left: 10px solid rgba(0,128,255,1) !important;
	}

	/*===================================CONTENT-LEFTMENU=========================================*/
	div#content #rightcolumn {
		position: unset !important;
		display: block !important;
		float: left !important;
		width: calc(100% - 271px) !important;
		padding: 0 !important;
		margin: 0 10px !important;
		border: none !important;
		text-align: unset !important;
		background: rgba(240,240,240,1) !important;
	}
	#rightcolumn .boxtitle {
		position: unset !important;
		padding: 10px 0 10px 5px !important;
		margin: 0 !important;
		height: 38px !important;
		line-height: 38px !important;
		font-family: "MuseoSnsW00-Cond300" !important;
		font-size: 1.1em !important;
		color: rgba(0,128,255,1) !important;
		border-bottom: 1px solid rgba(0,0,0,0.05) !important;
		text-transform: uppercase !important;
	}
	#rightcolumn .displaymode {
		padding: 0 !important;
		margin: 0 !important;
		background: rgba(0,0,0,0.05) !important;
		border: none !important;
		height: 55px !important;
	}
	#rightcolumn .videothumblist,
	#rightcolumn .starbox {
		height: auto !important;
		padding: 15px 0 !important;
		background: rgba(255,255,255,1) !important;
	}
	#rightcolumn .videothumblist,
	#rightcolumn .videothumblist .videos {
		display: block !important;
		text-align: left !important;
		padding: 0;
		margin: 0 !important;
	}
	#rightcolumn .videothumblist .videos .video,
	#rightcolumn .starbox .searchitem {
		display:  inline-block !important;
		overflow: hidden !important;
		position: relative !important;
		width: 154.5px !important;
		height: 260px !important;
		padding: 5px !important;
		margin: 8px 0 0 8px !important;
		border: none !important;
		box-shadow: 0.1px 0.1px 3px rgba(0,0,0,0.05) !important;
		text-align: center !important;
	}
	#rightcolumn .videothumblist .videos .video:hover,
	#rightcolumn .starbox .searchitem:hover {
		background: rgba(0,128,255,0.05) !important;
		box-shadow: 0.5px 0.5px 10px rgba(0,0,0,0.35) !important;
		transition: all 0.3s;
		
	}
	#rightcolumn .videothumblist .videos .video img {
		display: block !important;
		box-shadow: 0.2px 0.2px 5px rgba(255,255,255,1) !important;
		padding: 0 0 0 5px !important;
	}
	#rightcolumn .videothumblist .videos .video .id {
		display: block !important;
		position: absolute;
		bottom:  0 !important;
		font-size: 13.33px !important;
		color: rgba(0,128,255,1) !important;
		font-weight: bold !important;
		padding: 0 0 10px 0 !important; 
	}
	#rightcolumn .videothumblist .videos .video .title {
		display: block !important;
		position: absolute;
		bottom: 28px !important;
		padding: 0 !important;
		margin: 0 !important;
		width: calc(100% - 10px) !important;
		height: 22px !important;
		line-height: 22px !important;
		white-space: nowrap !important;
		text-overflow: ellipsis !important;
		overflow: hidden !important;
		float: unset !important;
		text-align: left !important;
		color: rgba(0,128,255,1) !important;
	}
	.displaymode .right {
		font-family: "MuseoSnsW00-Cond300" !important;
		font-size:  13.33px !important;
		color: rgba(0,128,255,1) !important;
		text-transform: uppercase !important;
	}
	
	
	#rightcolumn .page_selector {
		display: block !important;
		padding: 30px 0 0 0 !important;
		margin: 0 !important;
	}
	
	#rightcolumn .page_selector .page {
		display: inline-block !important;
		padding: 10px !important;
		margin:  0 3px !important;
		min-width:  20px !important;
		
		font-family: "MuseoSnsW00-Cond300" !important;
		font-size:  13.33px !important;
		color: rgba(0,0,0,0.75) !important;
		text-transform: uppercase !important;
		
		background: rgba(255,255,255,1) !important;
		
		border: none !important;
		border-radius: 2px !important;
		
		cursor: pointer !important;
	}
	
	#rightcolumn .page_selector span.current,
	#rightcolumn .page_selector .page:hover {
		background: rgba(0,128,255,1) !important;
		color: rgba(255,255,255,1) !important;
		transition:  all 0.3s;
		font-weight: bold !important;
	}
	
	
	#rightcolumn .pubgroup {
		display: table !important;
		table-layout: unset !important;
		margin: 0 auto !important;
		padding: 20px 0 0 0 !important;
		border: 1px solid rgba(0,0,0,0.25) !important;
	}
	#rightcolumn .pubgroup #header {
		background: transparent !important;
	}
	
	/**************************VIDEO DETAILS******************************/
	div#video_title {
		border-bottom: 1px solid rgba(0,0,0,0.05) !important;
		padding: 10px 0 10px  5px  !important;
		background: rgba(255,255,255,1) !important;
	}
	#video_title h3.post-title a,
	#video_title .text a {
		height: 38px !important;
		line-height: 38px !important;
		padding: 0 0 0 5px !important;
		height: inherit !important;
		line-height:  inherit !important;
		font-weight: bold !important;
	}
	#video_jacket_info {
		background: rgba(255,255,255,1) !important;
		border-spacing: 0 !important;
		width: 100% !important;
	}
	#video_jacket_info #video_jacket {
		padding: 10px !important;
		border-right: 1px dashed rgba(0,0,0,0.05) !important;
	}
	#video_jacket_info #video_info {
		text-align: unset !important;
		padding: 10px !important;
		margin: 0 !important;
		font-family: inherit !important;
		font-size: inherit !important;
		color: inherit !important;
	}
	#video_jacket_info #video_info .item {
		padding: 5px 0 !important;
		margin: 0 !important;
	}
	#video_jacket_info #video_info .item:hover {
		background: rgba(0,0,0,0.1) !important;
		transition: all 0.3s;
		border-radius: 2px !important;
	}
	#video_jacket_info #video_info .item table {
		margin: 0 !important;
		border: none !important;
	}
	#video_jacket_info #video_info td.header {
		text-align: unset !important;
	}

	div#video_favorite_edit {
		display: block !important;
		height: 45px !important;
		line-height: 45px !important;
		border-top: 1px solid rgba(0,0,0,0.05) !important;
		background: rgba(255,255,255,1) !important;
	}
	
	/**************************VIDEO REVIEWS AND COMMENTS******************************/
	div#video_reviews,
	div#video_comments {
		display: block !important;
		overflow: hidden !important;
		padding: 10px 0 !important;
		margin: 15px 0 !important;
		background: rgba(255,255,255,1) !important;
	}
	div#video_reviews *,
	div#video_comments * {
	  font-family: "MuseoSnsW00-Cond300" !important;
		font-size: 1.0em !important;
	}
	#video_reviews .header,
	#video_comments .header {
		font-family: "MuseoSnsW00-Cond500" !important;
		font-size: 1.25em !important;
		text-transform: uppercase !important;
		color:  rgba(0,128,255,1) !important;
		height: 38px !important;
		line-height: 38px !important;
		padding: 0 0 0 10px !important;
		margin: 0 !important;
		font-weight: bold !important;
	}
	#video_comments .header {
		border-bottom: 1px solid rgba(0,0,0,0.05) !important;
	}
	#video_reviews #video_reviews_alllang,
	#video_comments #video_comments_alllang {
		display: block !important;
		position: absolute !important;
		top: 0 !important;
		right: 0 !important;
		padding: 0 !important;
		margin: 0 !important;
		border: none !important;
		border-radius: 3px !important;
	}
	#video_reviews #video_reviews_alllang a,
	#video_comments #video_comments_alllang a {
		display: block !important;
		padding: 5px 10px !important;
		margin: 5px !important;
		border-radius: 3px !important;
		background: rgba(0,128,255,1) !important;
		color: rgba(255,255,255,1) !important;
		text-transform: uppercase;
	}
	#video_reviews #video_reviews_alllang a:hover,
	#video_comments #video_comments_alllang a:hover {
		background: rgba(0,180,255,1) !important;
		transition: all 0.3s;
	}
	
	#video_reviews table.review,
	#video_comments table.comment {
		display: table !important;
		width: 100% !important;
		margin: 0 !important;
		padding: 0 !important;
		border-top: none !important;
		border-bottom: 1px dashed rgba(0,0,0,0.25) !important;
		border-spacing: 0 !important;
	}
	
	#video_reviews table.review:hover,
	#video_comments table.comment:hover {
		background: rgba(0,128,255,0.02) !important;
		transition: all 0.3s;
	}
	
	#video_reviews table.review td.info,
	#video_reviews table.review td.date,
	#video_comments table.comment td.info,
	#video_comments table.comment td.date {
		width: 200px !important;
		border-right: 1px dashed rgba(0,0,0,0.25) !important;
		padding: 10px !important;
		overflow: hidden !important;
	}
	#video_reviews table.review td.t,
	#video_reviews table.review td.toolbar,
	#video_comments table.comment td.t,
	#video_comments table.comment td.toolbar {
		width: auto !important;
		padding: 10px 15px !important;
		margin: inherit !important;
	}
	
	/**************************STARBOX******************************/
	#rightcolumn .starbox {
		display: block !important;
		position: unset !important;
		background: rgba(255,255,255,1) !important;
		padding: 0 !important;
		margin: 10px 0 !important;
		width: 100% !important;
		height: auto !important;
		overflow: hidden !important;
		text-align: left !important;
	}
	#rightcolumn .starbox .searchitem {
		width: 159px !important;
		height: 183px !important;
		border-radius:  0 !important;
		text-align: right !important;
	}
	#rightcolumn .starbox .searchitem img {
		min-width: 159px !important;
		max-width: 159px !important;
		padding-bottom: 7px !important;
	}
	#rightcolumn .starbox .searchitem h3 {
		display: block !important;
		position: absolute !important;
		bottom: 0 !important;
		font-family: "MuseoSnsW00-Cond300" !important;
		color: rgba(0,128,255,1) !important;
		font-weight:  normal !important;
		margin: 0 !important;
	}
	
	/**************************CATEGORIES******************************/
	#rightcolumn .textbox {
		display: block !important;
		position: unset !important;
		border: 1px solid rgba(0,128,255,1) !important;
		background: rgba(255,255,255,1) !important;
		padding: 0 !important;
		margin: 10px 0 !important;
		width: 100% !important;
		overflow: hidden !important;
		text-align: unset !important;
	}
	#rightcolumn .textbox .boxtitle {
		background: rgba(0,128,255,1) !important;
		color: rgba(255,255,255,1) !important;
		border: 1px solid rgba(0,128,255,1) !important;
	}
	#rightcolumn .textbox .genreitem {
		display:  inline-block !important;
		width: 125px !important;
		text-align: left !important;
		padding: 8px 5px !important;
		margin: 5px 0 0 5px !important;
		background: transparent !important;
		border: none !important;
		border-radius: 0px !important;
		cursor: pointer !important;
	}
	#rightcolumn .textbox .genreitem:hover {
		transition: all 0.35s;
		border-left: 8px solid rgba(0,128,255,1) !important;
		background: rgba(0,0,0,0.05) !important;
	}
	
	/**************************ADVANCED SEARCH******************************/
	div#ui-accordion-accordion-panel-0 > p {
		display: none !important;
	}
	div#ui-accordion-accordion-panel-0 {
		display: block !important;
		padding: 10px 30px !important;
		margin: 0 !important;
	}
	#rightcolumn #accordion {
		background: rgba(255,255,255,1) !important;
	}
	#rightcolumn #accordion .searchbox {
		display: block !important;
		width: 100% !important;
		height: auto !important;
		position: unset !important;
		padding: 0 !important;
		margin: 15px auto !important;
		border: none !important;
	}
	div.searchbox .boxtitle,
	div.searchbox form {
		display: inline-block !important;
	}
	div.searchbox .boxtitle,
	div.searchbox input[type="text"],
	div.searchbox input[type="button"] {
	}
	div.searchbox .boxtitle {
		width: 240px !important;
		border-bottom: none !important;
	}
	div.searchbox input[type="text"] {
		width: 480px !important;
		height: 33px !important;
		background: rgba(0,0,0,0.01) !important;
		border: 1px solid rgba(0,128,255,1 ) !important;
	}
	div.searchbox input[type="button"] {
		width: 100px !important;
		height: 38px !important;
		margin-left: -8px !important;
		font-family: "MuseoSnsW00-Cond300" !important;
		color: rgba(255,255,255,1) !important;
		font-size: 1em !important;
		text-transform:  uppercase !important;
		background: rgba(0,128,255,1) !important;
		border: 1px solid !important;
		border-left: none !important;
		border-radius: 0 !important;
	}
	
  /*===================================LOGIN PANEL=========================================*/

	table.loginpanel {
		width: calc(100% - 50px) !important;
		margin: 0 auto !important;
	}
	/*
	table.loginpanel >tbody > tr > td:nth-of-type(2) {
		display: none !important;
	}
	*/
	table.loginpanel .leftp,
	table.loginpanel .rightp {
		top: 0 !important;
		width: 40% !important;
		padding: 0 !important;
		margin: 0 !important;
