css:
@-moz-document domain("userstyles.org") {
body{
	background-color: #333;
}
#nav-view,
#navigation,
#notes_textarea{
	background-color: #424242;
}
.navigation_title{
	color: #618bb5;
}
.linksBox:nth-child(1) .logo_navigation_link{
	filter: brightness(10);
}

.linksBox:nth-child(5) .logo_navigation_link{
	filter: brightness(10);
}
#install_style_button div{
	color: #2c3e50 !important;
}
#style_info div,
#style_info h1,
h1{
	color: #618bb5;
}
#install_style_button {
	box-shadow: 0 6px 16px 0 rgba(11, 84, 136, 0.35);
}
}
