css:
@-moz-document regexp("(http|https)://trello.com/.*") {
@font-face {
    font-family: 'siRoboto';
    src: url('https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2') format('woff2');
}
@font-face {
    font-family: 'Samim';
    src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.woff') format('woff');
}
@font-face {
    font-family: 'Vazir';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff');
}
@font-face {
    font-family: 'Vazir Bold';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff');
}
/* ========================================= */

body {
    font-family: Vazir, siRoboto, "Helvetica Neue", Arial, Helvetica, sans-serif !important;
    font-size: 12px;
}
body.body-board-view {
    background-color: rgba(0, 150, 210, 1.0)!important;
}
textarea {
    font-family: Samim, siRoboto!important;
    direction: rtl;
}
#surface:before {
    position: fixed;
    top: 0;
    left: 0;
    width: 0%;
    height: 0%;
    content: '';
    opacity: .95;
    /*    background-color: #ed1c24; */
    /*     background-image: url('http://unsplash.com/photos/MM5rpMpC9k4/download?force=true'); */
    /*     background-image: url('https://my.bmi.ir/portalserver/static/ibank/widgets/bmi-change-background-random/data/images/3.jpg'); */
    
    background-position: center center;
    background-size: cover;
    filter: blur(0px) brightness(65%) contrast(140%);
}
#header,
.body-board-view div#header {
    z-index: 10!important;
    background: rgba(238, 28, 36, .9)!important;
    box-shadow: 10px 2px 15px rgba(0, 0, 0, .1);
}
.header-btn.new-notifications {
    background: #ebb246;
}
.header-btn.new-notifications:hover {
    background: #dc8120;
}
.header-btn.new-notifications .header-btn-icon {
    color: #904808;
    animation: shakybumpy 8s ease-in-out .0s infinite normal;
}
.board-header {
    background: rgba(221, 12, 20, .9)!important;
}
.board-menu {
    position: fixed!important;
    z-index: 60!important;
    margin-top: 0!important;
}
.board-canvas {}
.list {
    border-radius: 10px!important;
}
.list-card-title {
    font-size: 13px;
    line-height: 1.7em;
}
.list-card-operation {
    right: auto;
    left: 3px;
    padding: 2px;
}
.checklist-item-details-text {
    direction: rtl;
text-align:right;
float:right;

}
.card-label.mod-card-front {
    float: right;
}
.board-wrapper.is-show-menu .board-header-btns.mod-right {
    right: -303px;
    visibility: visible;
}
/**
 * Animations
 * ========================== */

@keyframes shakybumpy {
    0% {
        transform: scale(1) rotate(0deg);
    }
    94% {
        transform: scale(1) rotate(0deg);
    }
    95% {
        transform: scale(1.05) rotate(0deg);
    }
    96% {
        transform: scale(1.05) rotate(20deg);
    }
    97% {
        transform: scale(1.05) rotate(-20deg);
    }
    98% {
        transform: scale(1.05) rotate(0deg);
    }
    99% {
        transform: scale(1) rotate(0deg);
    }
    100% {
        transform: scale(1) rotate(0deg);
    }
}
/******************** end of @-moz-document */
}
