css:
@-moz-document domain("www.mkse.com") {
.x-btn-widgetbar + div {
    display: none !important;
}
.x-btn-widgetbar + div + div {
    display: none !important;
}
.x-sidebar.right {
    display: none !important;
}
.x-main {
    width: 60% !important;
    margin: 0 auto;
    float: none;
}
.x-nav-articles {
    display: none !important;
}
#upprev_box {
    display: none !important;
}
.x-callout,
.wpsocialite,
.yarpp-related {
    display: none !important;
}
blockquote {
    padding-left: 30px;
}
blockquote p {
    margin: 0;
    font-size: 14pt;
}
.single-post .x-main.left .hentry .entry-featured {
    margin-right: 0;
    margin-top: 0;
}
.entry-featured .entry-thumb {
    text-align: center;
}
.entry-title {
    font-weight: 500;
    text-transform: none;
}
a[href='https://www.mkse.com/membership-join/membership-registration'] {
 	display: none   
}

a[href='https://www.mkse.com/membership-join/membership-registration'] + p {
 display: none;   
}

.x-post-carousel {
 display: none;   
}

}
