@-moz-document domain("aoschat.apple.com"), domain("chatsupport.apple.com") {
.chat-panel{
	width: 400px;
}

#chatWaitingMsg.supporting {
  font-size: 14px;
}

#product-help .productHeading, #order-help .orderHeading, #shopping-help .shoppingHeading{
	font-size: 15px;
}

#product-help .productDesc, #order-help .orderDesc, #shopping-help .shoppingDesc{
	font-size: 13px;
}

.chat-bubble{
	font-size: 13.2px;
	padding: 8px;
	margin-bottom: 12px;
	border: none;
	background-image: none;
	-moz-background-image: none;
	-webkit-background-image: none;
	text-shadow: 1px 1px 0 white;
	border-radius: 6px;
	-moz-border-radius: 6px;
	-webkit-border-radius: 6px;
}

.chat-bubble.agent{
	border: none;
	background-image: none;
	-moz-background-image: none;
	-webkit-background-image: none;
}

div#email_trans_form.email-form {
  margin-bottom: 20px;
  width: 100%;
}

#emailSuccessSentMessage {
  width: 100%;
}

.primary-panel .input{
	background: #edeef0;
	border: none;
  border-top: 1px solid #d2d2d2;
}

.overlay_routing_message{
  width: 369px !important;
}

.inQueueMessages{
  width: 398px;
}

.button.control{
	left: 50px;
	right: 50px;
}

.startChatButton{
	border-radius: 4px;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	left: 40px;
	right: 40px;
}

.onHoldScreen {
	width: fit-content;
	margin: 0 auto;
}

.persistentEmailContainer{
	width: 400px;
}
}
