css:
@-moz-document domain("medangle.com") {
.the-post-title > span {
    display: none;
}

.the-questions-answers p {
    width: fit-content;
}

.the-questions-answers p::before {
    color: #212121;
    border-radius: 100%;
    margin-right: 0.9vw;
    opacity: 0.8;
    height: 47px;
    width: 47px;
    text-align: center;
    display: flex;
    align-items: center;
    align-content: center;
    border: 1.5px solid #21ff97;
    font-family: 1;
    font-size: 1.1rem !important;
     /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
     /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
    transition: 0.1s ease-in;
}


.the-questions-answers p:nth-child(1)::after, .the-questions-answers p:nth-child(2)::after, .the-questions-answers p:nth-child(3)::after, .the-questions-answers p:nth-child(4)::after {
    display: none;
}

.the-questions-answers p:nth-child(1)::before {
    content: 'A';
}

.the-questions-answers p:nth-child(2)::before {
    content: 'B';
}

.the-questions-answers p:nth-child(3)::before {
    content: 'C';
}

.the-questions-answers p:nth-child(4)::before {
    content: 'D';
}

.the-questions-answers p:nth-child(5)::before {
    content: 'E';
}

.the-questions-answers p:hover::before {
    background: #21ff97;
    color: white;
}
}
