css:
@-moz-document domain("www.akiba-online.com") {
body,#header,.navTabs,.contentWrapper,.PanelScroller .scrollContainer,.discussionList, .memberList, .messageList .message.messageFullBlock, #ProfilePanes #info,.message .messageInfo,.nodeList .node.category,.nodeList .node, .nodeList .node.category.shortNode {background: #252525;color: #cccccc;}
.sectionMain, .primaryContent, .secondaryContent, .memberList, #recentActivity, .resourceList, #warnings>div[class=""], .LikeText,.message .editDate,.searchResult.primaryContent,.gridSection,.xenOverlay .section, .xenOverlay .sectionMain,#jumpMenu {background: #252525;color: #cccccc;}
.navTabs,.gridSection {box-shadow: 0 0 3px #cccccc}
.textHeading, .xenForm .sectionHeader,.xengallerySideBar .albumList .extraData a {color: #cccccc;}
.bbCodeBlock, .messageUserInfo,.xenOverlay .formOverlay, .xenOverlay #pageLogin,.textCtrl,.Menu .primaryContent,.Menu .secondaryContent,#QuickSearch.active,.redactor_toolbar,.mediaContainer,.rateBlock {background: #505050 !important;color: #cccccc !important;}
.redactor_toolbar li.redactor_btn_group ul {background: #cccccc !important;color: #cccccc !important;}
.xenForm fieldset, .xenForm .formGroup, .xenForm .ctrlUnit, .xenForm fieldset+.sectionHeader, .xenForm .ctrlUnit+.sectionHeader,.profilePage #NoProfilePosts,.albumContainer,.attachedFiles .attachmentList {background: #505050 !important;color: #cccccc !important;}
a:link,.navTabs .navLink,.bbCodeQuote .attribution,.searchResult .meta a {color:#6aa4f7}
.muted a,.sectionFooter, .likesSummary, .message .likesSummary.secondaryContent, .showcaseItem div.likesSummary {color: #6aa4f7 !important;}
body .faint, body a.faint,body .muted, body a.muted,.pairsJustified dt,.primaryContent,.searchResult .meta {color:#eeeeee !important;}
.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt,.xengallerySideBar .avatarList .extraData a,.searchResult .snippet a {color: #aaaaaa;}
a:visited {color: #72eca4;}
.navTabs .navLink,.navigationSideBar .section {background: #505050;}
.blendedEditor .redactor_box .redactor_btn_group ul, .blendedEditor .redactor_box iframe, .blendedEditor .redactor_box .redactor_smilies li {opacity: 1;}
.contentTagged.contentTagColor5 {color: #455c7d;}
.mediaContainer {border: 1px solid #757575; box-shadow: 0 0 0 0;}
.messageText {font-family: arial, sans-serif, tahoma}
#kiwi,#kiwi .server_select.initial:before {background: transparent !important; color: #cccccc !important;}
.discussionListItem.sticky {background: #2e3950 !important;}
.discussionListItem,.nodeList .forumNodeInfo {border-top: 1px solid #505050; border-bottom: 1px solid #505050;}
.discussionListItem .stats dl,.discussionListItem .stats dl:before,.discussionListItem .stats dl:after {border-left: 1px solid #505050; border-right: 1px solid #505050;}
.itemPageNav a,.sidebar .section .secondaryContent .footnote,.ctrlUnit>dd .explain,.sidebar .avatarList .userTitle {color: #cccccc;}
.discussionListItem .itemPageNav,.itemPageNav a {visibility: inherit; background: #505050;}
.discussionListItem:hover a,.nodeList .nodeList .node:hover a,.searchResult .main:hover h3 a {color: #eeeeee;}
.discussionListItem .title {font-family: arial, tahoma, verdana;}
.discussionListItem .posterDate {margin-top: 3px;}
}
