css:
@-moz-document domain("ask.fm") {
body {
    background-color: #eee !important;
}
#topMenu {
    background-color: #eee;
}
h3.section_title {
    color: #444;
}
.userItem_content span.userName {
    color: #666;
}
body footer .siteLinks a {
    color: #888;
}
.profileBio .icon-bio::before,
.profileBio .icon-location::before,
.profileBio .icon-link::before,
.profileBio .icon-interest::before,
.profileBio .hashtags-input::before,
.profileBio p,
.profileBio p .edit {
    color: #666;
}
.profileStats {
    margin-top: 22px;
}
.profileStats_number,
.profileStats_label {
    color: #666;
}
.askBox.section.rsp-eql-desktop {
    margin-top: 22px;
}
.profileBox_gradient {
    display: none;
}
.side-column footer .siteLinks a {
    color: #666 !important;
}
}
