css:
@-moz-document domain("www.youtube.com") {
#guide-icon.ytd-masthead {
    background-color: #238d98;
}
#logo-icon-container.ytd-topbar-logo-renderer {
    background-color: #3a25e4;
}
ytd-page-manager > *.ytd-page-manager {
    background-color: hsla(237, 100%, 50%, 0.45);
}
ytd-masthead[mode="legacy"] #container.ytd-masthead {
    background-color: #167ac6;
    background: #000000 url(http://countrafiles.ucoz.org/_ld/0/56.jpg) no-repeat fixed right top!important;
}
paper-button.yt-next-continuation {
    background-color: #b50e0e;
    color: white;
}
#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child) {
    background-color: #6d67d0a6;
}
#name.ytd-author-comment-badge-renderer {
    color: #ff1515;
}
paper-button * {
    color: #08dc22;
}
#label-icon.yt-dropdown-menu {
    background-color: #0cff1f;
}
#vote-count.ytd-comment-action-buttons-renderer,
.more-button.ytd-comment-renderer,
.less-button.ytd-comment-renderer {
    background-color: #01ffff;
}
yt-icon,
.yt-icon-container.yt-icon {
    background-color: #4e43f3;
}
paper-menu {
    background-color: #9e8dff;
}
#video-title.ytd-compact-radio-renderer {
    color: white;
}
/* Wallpaper */

.ytd-page-manager,
body.site-center-aligned {
    background: #000!important;
    background: #000000 url(https://images5.alphacoders.com/367/367207.jpg) no-repeat fixed right top!important;
}
/* Wallpaper */

#video-title.ytd-compact-video-renderer {
    color: #fdfdfd;
}
#byline.ytd-video-meta-block {
    color: #04ff23;
}
#metadata-line.ytd-video-meta-block span.ytd-video-meta-block {
    color: #04ff23;
}
#content-text.ytd-comment-renderer {
    color: #00e7ff;
    background-color: #171616c2;
}
.description.ytd-video-secondary-info-renderer yt-formatted-string.ytd-video-secondary-info-renderer {
    color: #00e7ff;
    background-color: #171616c2;
}
.title.ytd-video-primary-info-renderer,
span.yt-view-count-renderer {
    color: #00e7ff;
    background-color: #171616c2;
}
button[is="paper-icon-button-light"].ytd-toggle-button-renderer > yt-icon.ytd-toggle-button-renderer {
    background-color: red;
}
ytd-button-renderer.force-icon-button a.ytd-button-renderer {
    color: #03fd69;
}
ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer {
    color: #33b3e6;
}
a.yt-simple-endpoint.yt-formatted-string {
    color: #ad3;
    background-color: #bb0505;
}
.count-text.ytd-comments-header-renderer,
#icon-label.yt-dropdown-menu {
    color: #0cff40;
    background-color: #bb0505;
}
.input-content.paper-input-container textarea,
.date.ytd-video-secondary-info-renderer,
#author-text.yt-simple-endpoint.ytd-comment-renderer {
    color: #fff;
    background-color: #171616c2;
}
#simplebox-placeholder.ytd-comment-simplebox-renderer {
    color: #00e7ff;
    background-color: #171616c2;
}
#video-title.yt-simple-endpoint.ytd-grid-video-renderer {
    color: white;
}
#metadata-line.ytd-grid-video-renderer {
    color: aquamarine;
}
span.style-scope.ytd-shelf-renderer,
#video-title.ytd-video-renderer,
#channel-title.ytd-channel-renderer,
yt-formatted-string.style-scope.ytd-channel-renderer,
#metadata.ytd-channel-renderer,
#video-title.ytd-newspaper-hero-video-renderer,
.title.ytd-mini-channel-renderer,
#title.ytd-vertical-channel-section-renderer,
span.style-scope.ytd-newspaper-mini-video-renderer,
.content.ytd-metadata-row-renderer {
    color: #fff !important;
}
#description-text.ytd-video-renderer {
    color: #add;
}
div.style-scope.ytd-channel-renderer {
    color: #fc08fc;
}
#channel-header.ytd-c4-tabbed-header-renderer,
ytd-searchbox input {
    background-color: #255d5d;
}
paper-tabs.ytd-c4-tabbed-header-renderer {
    background-color: #737eb5;
}
#container.ytd-searchbox {
    background-color: #8c8c8c !important;
}
input.gsfi.ytd-searchbox {
    color: #ffff00 !important;
}
button.style-scope.ytd-searchbox {
    background-color: #22a4a8 !important;
}
paper-button.style-scope.ytd-subscribe-button-renderer {
    background-color: #b30202 !important;
}
span.deemphasize.style-scope.yt-formatted-string {
    color: #f5e900 !important;
}
#text.ytd-notification-multi-action-renderer {
    color: red;
}
yt-formatted-string.content.style-scope.ytd-channel-video-player-renderer,
span.style-scope.ytd-grid-channel-renderer {
    color: #ffffff !important;
}
yt-formatted-string.can-be-empty.style-scope.ytd-shelf-renderer {
    color: #4fff72 !important;
}
#video-title.ytd-compact-playlist-renderer {
    color: #00e7ff !important;
}
#message.ytd-message-renderer {
    color: #ff0202 !important;
}
#container.ytd-masthead {
    background-color: #091740d1;
}
div.header.style-scope.ytd-playlist-panel-renderer {
    background-color: #616161;
}
}

@-moz-document url("https://www.youtube.com/") {
#title.ytd-shelf-renderer {
    color: #ad1;
}
#details.ytd-grid-video-renderer a.ytd-grid-video-renderer {
    color: #ad1;
}
#metadata-line.ytd-grid-video-renderer span.ytd-grid-video-renderer {
    color: #adf;
}
}

@-moz-document url-prefix("https://www.youtube.com/results") {
#video-title.ytd-video-renderer,
#result-count.ytd-search-sub-menu-renderer,
#filter-group-name.ytd-search-filter-group-renderer,
ytd-search-filter-renderer yt-formatted-string.ytd-search-filter-renderer,
#video-title.ytd-playlist-renderer {
    color: white;
}
#title.ytd-exploratory-results-renderer,
#description-text.ytd-video-renderer,
#video-title.ytd-child-video-renderer,
#length.ytd-child-video-renderer {
    color: #00ff08;
}
}
