@-moz-document domain("123movies.co") {
body {

	background-color: #202020;
}

.top-menu li a {

	color: #fff !important;
}
.top-menu li.active>a {

	color: #fff !important;
}

.top-menu li a:hover {

	color: #73b353 !important;
}

.header {

    background-color: #242424 !important;
    margin-bottom: 20px;
    padding-bottom: 15px;
    background-image: none !important;
    box-shadow: 0 0 15px #000000 !important;
    border-bottom: 2px solid rgba(0,0,0,.7) !important;
}
.headerLogo {

	background-color: #fff !important;
    border-radius: 5px 5px 5px 5px !important;
}


.bottomfootera.container li a {

	color: #fff !important;
}


.bottomfootera.container li a:hover {
	color: #79c143 !important;
}

.footeronethird li a:hover {

    color: #79c143;

}

.sub-container {

	border-top: none;
    background-color: #393939;
    border-radius: 5px 5px 5px 5px;
    
}

.top-menu li ul.sub-menu li a {

	color: #fff !important;

}

.top-menu li ul.sub-menu li a:hover {

	border-radius: 5px 5px 5px 5px;
}



.onp-sl-secrets {
	
    border: none;
    display: none !important;

}

.single_epsiode_row_left p {

	color: #fff;
}

.espidoes_listings_area.smallContainer.clear {

	background-color: #363636;
    border-radius: 0px 0px 10px 10px;

}


.single_epsiode_row.clear {

	border-bottom: none;
}

.single_epsiode_row_right a {
	color: #fff;
    border-bottom: 3px solid #4d4d4d;
    background-color: #1e1e1e;

}
.search-content .form-control.search-input {
	border: transparent;
    background-color: #202020;
    border-radius: 10px 10px 10px 10px;
    color: #fff;
    

}
:focus {

    outline: none;
}

.topdescription {

	background: #363636;
    border-radius: 10px 10px 0px 0px;
    color: #fff;
    margin-top: 10px;
}

.videoPlayerBtn {

    background-color: #363636;
	border-radius: 0px 0px 10px 10px;

}

.topdescriptiondesc {

	border: none;
	
}
.chartdescriptionLeft li, .chartdescriptionRight li {
	color: #aaa;
}

.go_ratings_arearighttop.clear p {
	color: #fff;
}
.widgetsads.sidebaradsmainly {
	display: none;
}

.single_ads_player.clear {

	background-color: #363636;
}

.facebookComarea.smallContainer {
	
	background-color: #363636;
    border-radius: 0px 0px 10px 10px;
}

.footertop, .bottomfooter {

    background-color: #242424;

}

.mainfbComments {

	display: none;
}
.seacsonInfo {

	z-index: 4;

}

.qtip-default {

	background-color: #363636;

}

.jtip-top {

	background-color: #73B353;
}
.jtip-top .jt-info.jt-imdb {

	border-radius: 5px 5px 5px 5px;
    color: black;

}

.f-desc {

	color: #fff;
}

.qtip-content .block {

	color: #fff;
}
.pagination li span, .pagination li a {
	
    background-color: #357c00;
	color: #fff;
    font-weight: bold;

}
.tiptitle span {
    border-radius: 5px 5px 5px 5px;
}
.jtip-top {

color: #fff;

}

.pagination li.active span, .pagination li span:hover, .pagination li a:hover, .pagination li.active a {

	background-color: #4b4b4b;

}

li.disabled {

	display: none;
    
}

.filterAreac {

	background-color: #363636;
}

.filterRow ul li label {

	color: #fff;
}

.filterRow ul {

    background-color: #464646;
    border: 3px solid #464646;
    border-radius: 10px 10px 10px 10px;

}
.filterRow button:hover, .filterAreah, .top-user-content.guest .btn-successful, .requestsubmit {
	
	background-color: #357c00;    

}

.filterAreah {

	color: #fff;
}

.top-user-content.guest .btn-successful, .requestsubmit {
	
    border-color: #357c00;

}

.staticPage h1 {
	color: #fff;

}
.requestRow select, .requestRow input, .requestRow textarea {

    background-color: #742f2f;
	border: 1px solid #742f2f;
    border-radius: 5px 5px 5px 5px;
    color: #fff;
}

::-webkit-scrollbar { 
    display: none; 
}
#at4-share, .addthis_inline_share_toolbox_jd35 {
	display: none;
}

.bottomdescription {
	background-color: #464646;
    color: #fff;
    border-radius: 5px 5px 5px 5px;
}
.bottomdescription a {

	color: #73B353;

}
.bottomdescription a:hover {
	text-decoration: underline;
}

.chartdescriptionLeft li a:hover, .chartdescriptionRight li a:hover {

	text-decoration: underline;
}
img {

	border-radius: 10px 10px 10px 10px;
}

.top-menu li.active>a:hover {

	color: #fff!important;
}

.go_ratings_arealeft p {

    border-radius: 25%;
    
}

.vheadingmain p {


	border-radius: 10px 10px 10px 10px;
    
}

.videoPlayer {

	background: transparent!important;

}

.dsModalContainer {

	background-color: #202020!important;
}


.modal-body .form-control {

	background-color: #2c2c2c!important;
    color: #fff!important;
    box-shadow: 0px 0px 5px rgba(4, 2, 2, 0.3) inset!important;
    /*border: 2px solid rgba(139,54,54,.7) !important; */
	border: 1px solid rgba(4,2,2,0);
    border-radius: 5px 5px 5px 5px;

}

div.g-recaptcha {

	margin-top: 45px!important;

}

.go_front_serach_area.dsclear input {

	background-color: #2c2c2c!important;
	box-shadow: 0px 0px 5px rgba(4, 2, 2, 0.3) inset !important;
	color: #fff!important;
}


.logged .dropdown-menu {

	border-radius: 5px 5px 5px 5px!important;
	border: 3px solid #464646!important;
    background-color: #464646!important;
    padding-right: 5px!important;
	padding-left: 5px!important;
	padding-top: 5px!important;
    margin-top: 10px!important;

}

.logged .logged-user .dropdown-menu>li {

    border: none!important;
    background-color: rgba(48,48,48,0.82)!important;
	border-radius: 5px 5px 5px 5px!important;
	margin-bottom: 3px!important;
}
.logged .logged-user .dropdown-menu>li a {

	color: #fff!important;

}

.logged .logged-user .dropdown-menu>li:hover {

    border: none!important;
    background-color: rgba(102,102,102,0.82)!important;
	border-radius: 5px 5px 5px 5px!important;
	margin-bottom: 3px!important;
}

.profiles-wrap {

	background-color: transparent !important;

}

.profiles-wrap .sidebar {

    background-color: #363636!important;
	border-radius: 10px 0px 0px 10px!important;

}

.sidebar .sb-title {

	border-radius: 10px 0 0 0 !important;
}

.sidebar .sidebar-menu ul li a {

	color: #fff!important;

}


.sidebar .sidebar-menu ul li {

    border-color: rgba(0,0,0,0.35)!important;

}

.sidebar .sidebar-menu ul li.active {
	background-color: rgba(102, 102, 102, 0.82)!important;
}

.sidebar .sidebar-menu ul li.active a {

	color: #fff!important;
}


.sidebar .sidebar-menu ul li:hover {

 background-color: rgba(102, 102, 102, 0.82)!important;

}


.profiles-wrap .pp-main {

	background-color: #2a2a2a!important;
	border-radius: 0px 0px 10px 0px!important;
}


.user-content .uct-info .block {

	color: #fff!important;

}


.profiles-wrap .pp-main .ppm-head .ppmh-title {

	background-color: #00a898!important;
    border-color: #009789!important;
    border-radius: 0px 10px 0px 0px!important;

}

.mt20 .btn-successful {

    background-color: #b82d28 !important;
    border-color: #952d28!important;

}

.btn-successful:hover {

	opacity: 0.7!important;

}

.form-horizontal .control-label {

	color: #fff!important;

}
input[type="file"]{

	color: #fff!important;
}

.lazy.thumb.mli-thumb {
	
    color: #fff!important;

}

input[type="password"] {

	background-color: #333333!important;
	color: #fff!important;
	border-color: #202020!important;
	border-radius: 5px 5px 5px 5px!important;
}
