css:
@-moz-document domain("www.mimovrste.com") {
body {
	background-color: #0c0c0d;
}

.head {
	background-color: #38383d;
}

.nav-tertiary, .nav-tertiary a {
	color: #f9f9fa;
}

.fnl-item, .fnl-item a, .head a.user-logged-in, .link--secondary, .nav-customer-item-region, .nav-customer-item-region a, .nav-secondary a {
	color: #f9f9fa;
}

.nav-customer-item-region svg {
	filter: brightness(3);
}

.head-sitesearch input[type="search"] {
	background-color: #0c0c0d;
	color: #ffffff;
}

.form-sitesearch-inset {
	background-color: #38383d;
}

.form-sitesearch-suggest .nav-secondary--secondary {
	background-color: #0c0c0d;
}

.form-sitesearch .drw-content {
	color: #f9f9fa;
}

input, select, textarea {
	box-shadow: #38383d 2px 3px 3px -3px inset, #0c0c0d 0 -3px 3px -3px inset;
	background-color: #0c0c0d;
	color: #ffffff;
}

.head-sitesearch {
	border-radius: 3px;
}

.nav-customer-content {
	background-color: #737373;
}

.head .is-nav-customer-item-open .nav-customer-item-region, .is-nav-customer-item-open .nav-customer-item-region {
	background-color: #737373;
}

.main-wrapper {
	background-color: #0c0c0d;
}

.detail-buttons {
	background-color: #38383d;
}

.pnl, .pnl--notice {
	background-color: #38383d;
	color: #f9f9fa;
}

.bqt, .call-head, .con-h1, .con-h2, .con-h3, .foot-links .acc-item-head, blockquote, h1, h2, h3 {
	color: #ffffff;
}

.con-emphasize, .con-emphasize--bold, .nav-customer-content, .nav-customer-table-price {
	color: #f9f9fa;
}

.call, .call .call-head, .pro-name {
	color: #ffffff;
}

.lst-item {
	background-color: #38383d;
	border-color: #737373;
}

.lst-product-item-title a, .lst-product-item-price-value {
	color: #ffffff;
}

.nav-primary {
	background-color: #737373;
}

.content-tabs .panel-outer {
	background-color: #38383d;
}

.content-tabs .panel-inner {
	background-color: #737373;
	color: #ffffff;
}

.content-slider.for-footer, .promobox-bottom {
	background-color: #38383d;
}

.nav-step {
	background-color: #737373;
}

.nav-step-item {
	color: #f9f9fa;
}

.nav-step-item--active {
	background-color: #38383d;
}

.nav-step-item--active::after {
	border-left: 12px solid #38383d;
}

.cart-detail-item--active, .cart-detail-item--live:hover {
	background-color: #38383d;
	border-color: #38383d;
}

.cart-detail-foot {
	color: #ffffff;
}

.foot-categories-box .box-item {
	border-color: #38383d !important;
}

.foot-categories-box .box-item .con-h3 {
	color: #f9f9fa;
}

.promobox-phone-number {
	color: #f9f9fa;
}

.promobox-phone, .promobox-social {
	filter: brightness(3);
}

.foot {
	background-color: #0c0c0d;
}

}
