css:
@-moz-document domain("leho.howest.be") {
#header, #footer-wrap {
    background: #000000;
}

body {
    background-image: url("https://i.imgur.com/oV4BfuN.gif");
}

body {
    padding: 0 0 170px !important;
    font-family: monospace !important;
    color: #734646;
}

#corps {
    background: none;
    padding-top: 30px;
    padding-bottom: 60px;
}

#header.my_courses #breadcrumbs, #header.my_old_courses #breadcrumbs, #header.platform_admin #breadcrumbs, #header #breadcrumbs {
    background: #977a1c;
}

#corps #leftmenu li a.cursussenophalen {
    background: #977a1c;
}

#corps #leftmenu li a.cursusseninschrijven {
    background: #977a1c;
}

#corps #leftmenu li a.cursussenbeheren {
    background: #977a1c;
}

#corps #content h1, .user_course_category_header {
    background: rgb(206, 197, 138);
}

.ui-accordion-content .course a {
    color: #cec58a;
}

.ui-accordion-content .course {
    padding-top: 10px;
    padding-bottom: 10px;
    color: #734646;
    font-size: 0.7em;
}

.ui-widget-content {
    background: #1b0101;
    /* background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#FFFFFF), color-stop(100%,#F9F9F9)); */
}

#container #header #menu ul li.active a, #container #header #menu ul li a:hover {
    background: #382e0d;
}

#corps #content h1, .user_course_category_header {
    background: rgb(206, 197, 138);
    color: #1c0507;
    text-transform: uppercase;
    font-size: 1.2em;
    padding: 12px 16px;
}

#header.my_portal #breadcrumbs span {
    color: #ffffff;
}

#header.my_portal #breadcrumbs {
    background: #977a1c;
}

.boxedContent h2 {
    background: rgb(206, 197, 138);
    color: #FFFFFF;
    text-transform: uppercase;
    font-size: 1.2em;
    padding: 12px 16px;
}

.boxedContent {
    background: #1b0101;
    margin-bottom: 20px;
}

.boxedContent h3 {
    color: #cec58a;
    text-transform: uppercase;
    font-size: 1.111em;
    padding-bottom: 10px;
    border-top: 1px solid #4f8fbb;
    padding-top: 20px;
}

a:visited, a:hover {
    color: #cec58a;
    /* background-color: #FFF02A; */
    text-decoration: none;
}
a, a:link {
    color: #cec58a;
    text-decoration: none;
}

strong {
    font-weight: bold;
    color: #734646;
}

#tools .tool {
    background-color: #977a1c;
}

#announcementlist .announcement .announcement_content .visible {
    clear: both;
    background-color: #545141;
}

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 1px solid #cccccc;
    font-weight: bold;
    color: #000000;
    background-color: #cec58a;
}

#announcementlist .announcement .announcement_title {
    background: rgb(206, 197, 138);
    border: 1px solid rgba(255,255,255,0);
    padding-bottom: 8px;
}
}
