css:
@-moz-document domain("soendervangskolen-hammel.m.skoleintra.dk") {
.sk-l-header {
      background: #1a1a1a !important;
      box-shadow: 0px 1px 10px 0px #000 !important;
}
#sk-personal-menu-button {
      transition: .5s !important;
      -webkit-transition: .5s !important;
}
#sk-personal-menu-button:hover,
#sk-personal-menu-button:active {
      background-color: #696969 !important;
}
.ccl-dropdownmenu-menuitem-selected {
      background-color: #696969 !important;
}
body {
      background-color: #222 !important;
      color: #ccc !important;
}
.sk-user-input strong span {
      background-color: #ccc !important;
      color: #000 !important;
}
.sk-news-item {
      background-color: #1a1a1a !important;
      box-shadow: 0px 1px 10px 0px #000 !important;
}
.sk-profile-image-icon {
      background-color: #565656 !important;
}
.sk-homepage-week-section-header span {
      color: #ccc !important;
}
.sk-homepage-week-section-header a:before,
.sk-homepage-week-section-header span {
      color: #ccc !important;
}
#sk-personal-menu-container ul li {
      background-color: #2d2d2d !important;
}
#sk-personal-menu-container {
      background-color: #696969 !important;
}
.sk-homepage .sk-homepage-rightsidebar .sk-homepage-week-section ul li>a {
      color: #ccc !important;
}
.sk-homepage .sk-homepage-rightsidebar .sk-homepage-week-section ul li>a:before {
      color: #ccc !important;
}
.sk-personal-menu-item-settings:hover {
      background-color: #555 !important;
}
.sk-personal-menu-item-classic:hover {
      background-color: #555 !important;
}
.sk-personal-menu-item-logout:hover {
      background-color: #555 !important;
}
.ul.sk-page-tabs-container,
.ccl-pagetabs .active,
.ccl-pagetabs li.active a:hover {
      background-color: #313131 !important;
}
ul.sk-page-tabs-container>li.sk-page-tabs-item.sk-page-tabs-item-active h2:after {
      background-color: #737373 !important;
}
ul.sk-page-tabs-container>li.sk-page-tabs-item.sk-page-tabs-item-active h2 {
      color: #ccc !important;
}
ul.sk-page-tabs-container {
      background-color: #313131 !important;
      border-bottom: 1px #737373 solid !important;
}
.sk-custom-radio.checked:after {
      background: #737373 !important;
}
.ccl-button.sk-button-blue,
a.ccl-button:active.sk-button-blue,
a.ccl-button:focus.sk-button-blue,
a.ccl-button:link.sk-button-blue,
a.ccl-button:visited.sk-button-blue {
      background-color: #737373 !important;
      border: #ccc !important;
}
.sk-l-layout-body .sk-l-menu-container,
.sk-l-layout-body .sk-l-mobile-menu-container {
      background-color: #313131 !important;
}
.sk-l-layout-body .sk-l-menu-container ul>li a.sk-l-active-menu-item:active,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a.sk-l-active-menu-item:active,
.sk-l-layout-body .sk-l-menu-container ul>li a.sk-l-active-menu-item:focus,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a.sk-l-active-menu-item:focus,
.sk-l-layout-body .sk-l-menu-container ul>li a.sk-l-active-menu-item:hover,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a.sk-l-active-menu-item:hover,
.sk-l-layout-body .sk-l-menu-container ul>li a.sk-l-active-menu-item:link,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a.sk-l-active-menu-item:link,
.sk-l-layout-body .sk-l-menu-container ul>li a.sk-l-active-menu-item:visited,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a.sk-l-active-menu-item:visited {
      background-color: #ccc !important;
}
.sk-l-layout-body .sk-l-menu-container ul>li a:hover,
.sk-l-layout-body .sk-l-mobile-menu-container ul>li a:hover {
      background: rgba(204, 204, 204, 0.6) !important;
      color: #000 !important;
}
h2 {
      color: #ccc !important;
}
.sk-multipicker {
 	background-color: #737373 !important;     
}
.sk-custom-checkbox {
 	background-color: #ccc !important;     
}
.sk-custom-checkbox.checked {
 	background-color: #737373 !important;
      border-color: #ccc !important;
}
.sk-grid, .sk-list {
 	background-color: #222 !important;     
}
.ccl-rwgm-column-1-3 sk-message-senderrecipient-name {
 	color: white !important;     
}
div.ccl-rwgm.sk-grid>ul {
      border-bottom: #ccc !important;     
}
ul.ccl-rwgm-row>li {
 	color: #ccc !important;     
}
.sk-pretty-feedback.sk-static-feedback.ccl-feedback-information {
 	background-color: #313131 !important;  
      border-color: #353535 !important;
      box-shadow: 0px 1px 4px 0px #000 !important;
      color: #ccc !important;
}
.sk-highlight {
 	background-color: #4a4a4a !important;
}
.sk-searchsubmit-container .sk-searchsubmit-button {
 	background-color: #737373 !important;     
}
.sk-message-item .sk-message-item-content {
 	background-color: #1a1a1a !important;     
}
.ccl-button.sk-button-white, a.ccl-button:active.sk-button-white, a.ccl-button:focus.sk-button-white, a.ccl-button:link.sk-button-white, a.ccl-button:visited.sk-button-white {
 	background-color: #737373 !important;
      border-color: #787878 !important;
      color: #222 !important;
}
.sk-button-style-message-reply:before {
 	color: #222 !important;      
}
.sk-button-style-message-forward:before {
	color: #222 !important;      	     
}
.sk-button-style-mark-message-unread:after {
	background-color: #999 !important;
}
.ccl-button.sk-button-red-inversed, a.ccl-button:active.sk-button-red-inversed, a.ccl-button:focus.sk-button-red-inversed, a.ccl-button:link.sk-button-red-inversed, a.ccl-button:visited.sk-button-red-inversed {
 	background-color: #737373 !important;   
      color: #222 !important;
      border-color: #787878 !important;
}
.ccl-button.sk-button-grey[disabled], a.ccl-button:active.sk-button-grey[disabled], a.ccl-button:focus.sk-button-grey[disabled], a.ccl-button:link.sk-button-grey[disabled], a.ccl-button:visited.sk-button-grey[disabled] {
 	background-color: #ccc !important;
      color: #aaa!important;
}
.sk-button-style-previous:before {
 	color: #aaa!important;     
}
.ccl-cssmodal a.ccl-cssmodal-modal-close:after {
      background-color: #737373 !important;
      color: #ccc !important;
}
.ccl-button.sk-button-red, a.ccl-button:active.sk-button-red, a.ccl-button:focus.sk-button-red, a.ccl-button:link.sk-button-red, a.ccl-button:visited.sk-button-red {
 	background-color: #737373 !important;
      border-color: #222 !important;
      color: #ccc !important;
}
.ccl-button.sk-button-white:visited, a.ccl-button:active.sk-button-white:visited, a.ccl-button:focus.sk-button-white:visited, a.ccl-button:link.sk-button-white:visited, a.ccl-button:visited.sk-button-white:visited, .ccl-button.sk-button-white:active, a.ccl-button:active.sk-button-white:active, a.ccl-button:focus.sk-button-white:active, a.ccl-button:link.sk-button-white:active, a.ccl-button:visited.sk-button-white:active, .ccl-button.sk-button-white:link, a.ccl-button:active.sk-button-white:link, a.ccl-button:focus.sk-button-white:link, a.ccl-button:link.sk-button-white:link, a.ccl-button:visited.sk-button-white:link {
 	background-color: #737373 !important;
      border-color: #222 !important;
      color: #ccc !important;    
}
.ccl-cssmodal .ccl-cssmodal-modal-inner {
	background-color: #676767 !important;
}
.ccl-cssmodal .ccl-cssmodal-modal-footer {
 	background-color: #565656 !important;     
}
.ccl-cssmodal .ccl-cssmodal-modal-header {
	background-color: #1a1a1a !important;
}
.sk-custom-select-wrapper select {
 	background-color: #555;     
}
.sk-new-message-add-recipients-dialog .ccl-cssmodal-modal-content {
 	background: #222 !important;     
}
.ccl-formcontainer .sk-forms-buttonspanel-fixed-bottom {
 	background-color: #1a1a1a !important;     
}
ul.sk-list.sk-linsk-list>li>a:active, ul.sk-list.sk-linsk-list>li>a:focus, ul.sk-list.sk-linsk-list>li>a:hover, ul.sk-list.sk-linsk-list>li>a:link, ul.sk-list.sk-linsk-list>li>a:visited {
 	color: #ccc !important;     
}
.sk-grid .sk-unread-item, .sk-list .sk-unread-item {
 	background-color: #4a4a4a !important;     
}
.sk-weekly-plan-container .sk-weekly-plan-grid-cell.sk-weekly-plan-header {
 	background: #1a1a1a !important;     
}
.sk-weekly-plan-container .sk-weekly-plan-grid-cell {
      border-radius: 0px !important;
      background-color: #1a1a1a !important;
}
.ccl-button.sk-button-blue-inversed, a.ccl-button:active.sk-button-blue-inversed, a.ccl-button:focus.sk-button-blue-inversed, a.ccl-button:link.sk-button-blue-inversed, a.ccl-button:visited.sk-button-blue-inversed {
 	background-color: #737373 !important;
      border-color: #222 !important;
      color: #ccc !important;
}
.ccl-formcontainer.sk-forms-container-no-bg {
 	background-color: #1a1a1a !important;     
}

.sk-reply-message-container .sk-new-message-title, .sk-new-message-container .sk-new-message-title {
 	padding: 15px 40px 15px 40px !important;     
}
.sk-recipients-multiselect-root .sk-recipients-multiselect-input-block {
 	background-color: #555 !important;     
}
.sk-reply-message-container .sk-new-message-text-input, .sk-new-message-container .sk-new-message-text-input {
 	background-color: #555 !important;     
}  
.cke_top {
 	background-color: #555 !important;
      border-bottom: #000 !important;
}
.cke_combo_button span, .cke_button span:before, .cke_button span:after {
 	color: #222 !important;     
}
.sk-grid .sk-grid-top-header, .sk-list .sk-grid-top-header {
 	background-color: #1a1a1a !important;
}
.sk-documents-list {
 	box-shadow: 0px 1px 10px 0px #000 !important;     
}
.sk-documents-title-column {
 	color: #ccc !important;     
}
.sk-documents-list .ccl-rwgm-row.sk-selected-document {
 	background: #737373 !important;     
}
.sk-documents-list .ccl-rwgm-row:not(.sk-grid-top-header):hover {
	background: #737373 !important;
}
.ccl-formcontainer {
 	background-color: #313131 !important;     
}
.sk-pretty-feedback.ccl-feedback-information {
 	background: #313131 !important;
      border-color: #222 !important;
}
.sk-white-box {
 	background: #313131 !important;     
}
.sk-contact-card .section .photo-block {
 	display: none !important;     
}
