css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
@-moz-document domain("azlyrics.com") {
*/
@-moz-document url-prefix("https://www.azlyrics.com/lyrics/") {
  .main-page {
    color: white;
    background-color: black;
    font-family: Ubuntu;
    font-size: 2.5em;
  }
  
  .navbar-default {
    background-color: #333;
    border: none;
  }
  
  .navbar-default .navbar-toggle {
    background: gray;
    border: none;
  }
  
  .navbar-brand img {
    display: none;
  }
  
  .footer-wrap {
    display: none;
  }
  
  .navbar-footer,
  .navbar-bottom {
    display: none;
  }
  
  .smt,
  ol.noprint,
  .album-panel,
  form.search,
  .lboard-wrap,
  .navbar-default,
  .div-share
  {
    display: none;
  }
  
  .lyricsh h2 {
    font-size: 1.1em;
  }
  
  body {
    background: black;
  }
}
