css:
@-moz-document domain("e.zhp.pl") {
body {
	background-color: #f2f2f2;
	font-family: arial;
    padding-bottom: 50px;
}

a {
    font-size: 13px;
}

a, a:visited, a:active {
    font-weight: bold;
}

input, a {
	outline: none;
}

.icon {
	padding-right: 0px;
}

#content {
    margin-top: 100px;
}
}

@-moz-document domain("e.zhp.pl") {
.ctFormContTD {
    padding: 0px;
    background: transparent;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
}

#MainContent_ctListaOsoba_TC10 {
	text-align: right;
}

#MainContent_ctListaOsoba_TC10 > div {
	display: inline-block;
    margin-right: 5px;
}

#MainContent_UpdatePanel2 > table,
#MainContent_ctListaOsoba_TC10 > div {
    margin: 5px;
    padding: 10px;
    background: #fff;
    
    border: 1px solid #eaeaea !important;
    
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
}
}

@-moz-document domain("e.zhp.pl") {
#headerside {
	background-color: #1a1a1a;
}

#headtitle {
    min-width: 400px;
    width: 400px;
    height: 30px;
    
    padding: 12px;
}

#headlogininfo div.inside {
	max-width: 500px;
    width: 500px;
}

#headlogininfo > div.inside > table {
	font-size: 10px !important;
}

#headlogininfo a, #headlogininfo a:hover {
	color: #ddd;
}


#menuside {
	background-color: #fff;
    height: 40px;
    border-bottom: 1px solid #eaeaea;
}

div.menu {
    height: 30px;
}

.menulevel1 {
    height: 25px;
    padding-right: 10px;
    margin-right: 30px;
    outline: none;
}

.menulevel1 .icon {
    margin-top: 3px;
    width: 20px;
}

ul.level2.dynamic {
    border-color: #e2e2e2;
    border-radius: 3px;
}

.menulevel2 {
    background-color: #fff;
    color: #666;
    font-size: 13px;
    width: 250px;
    outline: none;
}

div.menu ul li a:hover {
    color: #333;
}
}

@-moz-document domain("e.zhp.pl") {
#SwitchPanel {
    top: 52px;
}

.lblSwPan {
    line-height: 25px;
    font-size: 12px;
    color: #333;
}

.ddlSwPan {
    color: #333;
    font-size: 12px;
    line-height: 28px;
    height: 28px;
    
    background-color: #f1f1f1;
    
	border: none;
    
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
}
}

@-moz-document domain("e.zhp.pl") {
#FormAuth {
    max-width: 480px;
	margin: auto;
    margin-top: 160px;
    border: 1px solid #e2e2e2;
}

.divHead {
    display: block;
    width: auto;
    
    margin: 30px;
    margin-top: 25px;
    margin-bottom: 20px;
    
    padding-bottom: 10px;
    border-bottom: 1px solid #efefef;
    background-color: transparent;
    
    font-size: 18px;
    font-weight: bold;
    color: #666;
    text-shadow: none;
}

.divAuth {
	text-align: right;
}

.txtAuth {
	color: #666;
    font-size: 20px;
    line-height: 25px;
}

.divLinks {
	margin-top: 10px;
	margin-bottom: 10px;
}

.divAuthInfo {
	font-size: 13px;
}

.linkAuth {
	text-shadow: none;
    font-size: 16px;
    color: #666;
    font-weight: bold;
}

.linkAuth:hover {
    color: #333;
}

#MainContent_ctFormChangePassword_TRID .divAuth {
    text-align: left;
}
}

@-moz-document url("https://e.zhp.pl/SitePages/Auth/?chPass=true") {
#FormAuth > table > tbody > tr:nth-child(2) > td {
	display: none;
}

.divHead {
    margin-left: 0px;
    margin-right: 0px;
    margin-top: 5px;
}
}

@-moz-document domain("e.zhp.pl") {
.btnAuth, .popupButtons input,
#MainContent_ctrlProcOsoUsun_lbtnDoIt {
    font-size: 16px;
    text-transform: none;
    text-shadow: none;
    font-weight: bold;
    
    cursor: pointer;
    
    background-color: #f2f2f2;
    background-image: -webkit-linear-gradient(top, #f5f5f5, #f1f1f1);
    border: 1px solid #f2f2f2;
    border-bottom: 1px solid #f2f2f2;
    color: #555 !important;
    
    line-height: 25px;
    padding: 5px 10px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
}

.btnAuth:hover, .popupButtons input:hover,
#MainContent_ctrlProcOsoUsun_lbtnDoIt:hover {
    background-image: -webkit-linear-gradient(top, #f8f8f8, #f1f1f1);
    background-color: #f8f8f8;
    border: 1px solid #c6c6c6;
    color: #333 !important;
}
}

@-moz-document domain("e.zhp.pl") {
.popupContainer {
	background-color: #fff;
    border-radius: 5px;
    border: 0;
}

.popupTitlebar {
	border-radius: 0;
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
    background: #666;
}

.popupBody {
    padding: 10px;
    padding-bottom: 5px;
    
	border: 0 #aaa solid;
    border-left-width: 1px;
    border-right-width: 1px;
}

.popupButtons {
    margin: 0px;
    padding: 10px;
	border: 1px #aaa solid;
    border-top: 0;
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}

.popupButtons input {
	font-size: 13px;
    margin: 0;
    line-height: 23px;
}

.popupButtons input:hover {
    background-image: -webkit-linear-gradient(top, #f8f8f8, #f1f1f1);
    background-color: #f8f8f8;
    border: 1px solid #c6c6c6;
}

.popupButtons hr {
    display: none;
}

.ModalPopupBG {
    background-color: #aaa;
    opacity: 0.6;
}
}

@-moz-document domain("e.zhp.pl") {
#gv {
	border: 0;
    padding: 0;
}

#gv a {
    font-size: 13px;
	color: #666;
}

#gv a:hover {
    font-size: 13px;
	color: #333;
}

.gvTable {
    font-size: 13px;
    color: #333;
}

.gvHeadTh {
    background: #999;
    color: #fff;
    font-size: 13px;
    border: 0;
}

.gvHeadTh th {
    height: 30px !important;
    vertical-align: middle;
}

.gvHeadTh a {
    color: #fff;
}

.gvItemTd {
    border: 0;
	background-color: #efefef;
}

.gvItemTd td {
    padding: 3px 5px;
}
}

@-moz-document domain("e.zhp.pl") {
.gvSelItemTd, .gvAltItemTd, .gvItemTd {
	border: none;
    border-bottom: 1px solid #ddd;
}

.gvAltItemTd {
	background-color: #fdfdfd;
}
 
.gvItemTd {
	background-color: #f5f5f5;
}

.gvSelItemTd {
	background-color: #e1e1e1;
}
}

@-moz-document domain("e.zhp.pl") {
#MainContent_UpdatePanel2 > table {
    box-shadow: none !important;
}

#MainContent_UpdatePanel2 > table > tbody > tr:nth-child(1) > td:nth-child(1) > div {
	border: 1px solid transparent !important;
}

#MainContent_UpdatePanel2 > table > tbody > tr:nth-child(3) > td {
	padding-top: 15px;
}

#MainContent_UpdatePanel2 > table > tbody > tr:nth-child(1) > td {
    padding-bottom: 10px;
}

.tdViewHead, .tdViewSubHead {
    color: #666;
}

.tdViewSubHead {
	padding-top: 5px;
    margin-bottom: 3px;
    border-bottom: 1px solid #ddd;
}

.PersonViewHead, .PersonViewValue {
	color: #333;
    padding-top: 2px;
}

.PersonViewHead {
	font-weight: bold;
}
}

@-moz-document domain("e.zhp.pl") {
#MainContent_tblProcUsun > tbody > tr:nth-child(1) > td > span {
	font-size: 14px;
    font-weight: bold;
}

#MainContent_ctrlProcOsoUsun_lblInfo {
	color: #333 !important;
}

#MainContent_ctrlProcOsoUsun_lbtnDoIt {
	font-size: 14px !important;
}


.tblRound {
    margin: 5px;
    margin-top: 10px;
    
    padding: 10px;
    background-color: #fff;
    
	border-color: #eaeaea;
    border-radius: 3px;
    box-shadow: none;
}

.tblRound div > table {
    margin-top: 10px;
    font-size: 12px;
}

.dvHead {
	width: 110px;
    
    font-weight: bold;
    border-bottom: 1px solid #ddd;
}

.dvHead, tr > .dvValue {
    height: 30px;
    padding: 2px 6px;
    
    font-size: 12px;
    color: #333;
    
	border: none;
}

tr > .dvValue {
    background-color: #fff;
    border-bottom: 1px solid #eaeaea;
}

tr:first-child > .dvValue {
    border-top: 1px solid #eaeaea;
}

tr:first-child .dvHead {
    border-top: 1px solid #ddd;
}

.dvValue select, .dvValue input {
    height: 21px;
    padding: 2px 3px;
    
    font-size: 12px;
    color: #333;
	background-color: #f1f1f1;
    border: none;
}

.dvValue select {
	height: 25px;
}

.dvValue input[type=text] {
    min-width: 70px;
}
}

@-moz-document domain("e.zhp.pl") {
#footer {
	background-color: #1a1a1a;
}

.tblMainMinWTdInfo > *,
#footer > table > tbody > tr > td:nth-child(2) > a {
    font-size: 14px !important;
	color: #ddd !important;
}

#footer > table > tbody > tr > td:nth-child(2) > a:hover {
	color: #fff !important;
    cursor: pointer;
}


.tblMainMinWTdCopR {
	width: 270px;
}

.tblMainMinWTdCopR:after {
	content: 'Wygląd zaprojektowany przez Drego31';
    display: block;
}
}
