css:
@-moz-document domain("the-flow.ru") {
/*ВСРАТОЧКА МОЯ*/

header {
    will-change: box-shadow;
    -webkit-box-shadow: 0 1px 8px rgba(0, 0, 0, .3);
    box-shadow: 0 1px 8px rgba(0, 0, 0, .3);
    padding: 0 0 0px;
    margin-bottom: 30px;
    background: none;
    transition: box-shadow 0.2s ease-out;
}
header .logo {
    will-change: all;
    transition: all 0.41s ease-in-out;
}
.fixed_header header .logo {
    width: 112px;
}
.header_fields {
    will-change: padding;
    transition: padding .2s ease-out;
}
.fixed_header header {
    box-shadow: none!important;
    background-color: #fff;
}
.content__right_column_main_page {
    display: none!important;
}
.homepage_slider.main_page {
    width: 100%;
}
.homepage_slider li {
    width: 1260px!important;
}
.homepage_slider .btn_next,
.homepage_slider .btn_prev {
    height: 100%;
    top: 0;
    margin-top: 0px;
}
.publication__item:hover {
    -webkit-box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
}
.publication__item {
    will-change: box-shadow;
    transition: box-shadow 0.1s ease-in-out;
    -webkit-box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    box-shadow: 0 1px 4px 0 rgba(0, 0, 0, 0.14);
    display: inline-block;
    width: 350px;
    margin: 0px 20px;
    margin-bottom: 25px;
    padding: 10px;
    vertical-align: top;
    letter-spacing: normal;
    font-size: 14px;
    line-height: 1.35em;
}
.publication__item.shortnews {
    display: none;
}
.publication__item-photo {
    width: 370px;
    margin-left: -10px;
}
.hover_shadow:hover:after {
    content: none!important;
}
.publication__item-title {
    font-size: 17px;
    margin: 10px 0;
}
.publication__item-text {
    font-size: 15px;
}
.btn_to_top {
    will-change: box-shadow;
    transition: box-shadow 0.1s ease-in-out;
    position: fixed;
    left: inherit;
    bottom: 32px;
    right: 32px;
    cursor: pointer;
    background-color: #fff;
    width: 56px;
    height: 56px;
    display: none;
    background-size: 30px;
    -webkit-border-radius: 50%;
    border-radius: 50%;
    -webkit-box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.3);
    box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.3);
}
.btn_to_top:hover {
    -webkit-box-shadow: 0 2px 3px 0 rgba(0, 0, 0, 0.3);
    box-shadow: 0 2px 3px 0 rgba(0, 0, 0, 0.3);
}
.btn_to_top:active {
    box-shadow: inset 0px 3px 8px -3px #333;
}
.homepage_slider__text {
    width: 1260px;
}
.homepage_slider__text.bottom>div {
    margin: 0 50px;
    width: 1160px!important;
    max-width: 1160px;
    position: absolute;
    bottom: 50px;
    text-shadow: 2px 3px 0px #000;
}
.items {
    margin: 0px 0px 0px 35px;
}
.wrapper-sticky {
    display: none!important;
}
.content__left_column {
    float: left;
    width: 100%!important;
}
.age_restriction {
    display: none!important;
}
.btn_to_top {
    animation-name: btn;
    animation-duration: 1s;
}
@keyframes btn {
    0% {
        bottom: -88px;
        transform: scale(0);
        box-shadow: 0 0px 0px 1px rgb(0, 0, 0);
    }
    25% {
        bottom: 32px;
        transform: scale(0.5);
        box-shadow: 0 0px 0px 1px rgb(0, 0, 0);
    }
    50% {
        bottom: 40px;
        transform: scale(1.4);
        box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0.3);
    }
    90% {
        transform: scale(1);
        box-shadow: 0 0px 1px 0 rgba(0, 0, 0, 0.3);
    }
    100% {
        bottom: 32px;
        transform: scale(1);
        -webkit-box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.3);
        box-shadow: 0 6px 10px 0 rgba(0, 0, 0, 0.3);
        opacity: 1;
    }
}
.homepage_slider .btn_prev {
    will-change: box-shadow;
    transition: box-shadow 0.1s ease-in-out;
    height: 100%;
    top: 0;
    margin-top: 0px;
    border-radius: 0 36px 36px 0;
    background-color: #e5e5e5cc;
}
.homepage_slider .btn_next {
    will-change: box-shadow;
    transition: box-shadow 0.1s ease-in-out;
    height: 100%;
    top: 0;
    margin-top: 0px;
    border-radius: 36px 0 0 36px;
    background-color: #e5e5e5cc;
}
.homepage_slider .btn_next:hover,
.homepage_slider .btn_prev:hover {
    box-shadow: -1px 0 2px rgba(0, 0, 0, 0.5);
}
}
