/* ==UserStyle==
@name           AllanPls for Path of Exile Trade
@namespace      github.com/openstyles/stylus
@version        0.1.5-rc.3
@description    AllanPls for Path of Exile Trade
@author         survfate (/u/survfate)
==/UserStyle== */

@-moz-document domain("pathofexile.com") {
/* page layout */
#trade .results:not(.compact) .resultset
{
    max-width: 1000px;
    margin-left: auto;
    margin-right: auto;
}
#trade .search-bar .search-advanced-pane
{
    width: 100%;
}
#trade .search-bar .search-advanced-pane.blue > .filter-group
{
    width: 50%;
    float: left;
}
#trade .search-bar .search-advanced-pane.blue > .filter-group .form-control.socket.str
{
    border-bottom-color: red;
}
#trade .search-bar .search-advanced-pane.blue > .filter-group .form-control.socket.dex
{
    border-bottom-color: green;
}
#trade .search-bar .search-advanced-pane.blue > .filter-group .form-control.socket.int
{
    border-bottom-color: blue;
}
#trade .search-bar .search-advanced-pane.brown
{
    /* width: 50%; */
    /* margin: 0 auto; */
    /* float: none; */
}
#trade .results:not(.compact) .resultset .row
{
    display: grid;
    grid-template-columns: 0% 30% 70%;
    /* grid-template-rows: 50% 50%; */
    padding-right: 15px;
}
#trade .results:not(.compact) .resultset .row:not(.exchange)
{
    grid-template-rows: 50% 50%;
}
/* row layout */
#trade .results:not(.compact) div.left,
#trade .results:not(.compact) div.middle,
#trade .results:not(.compact) div.right
{
    width: 100%;
}
#trade .results:not(.compact) div.right
{
    grid-column: 2;
}
#trade .results:not(.compact) div.middle
{
    grid-column: 3;
    grid-row-start: 1;
    grid-row-end: span 2;
}
#trade .results:not(.compact) div.right .details
{
    margin-top: 10px;
}
#trade .results:not(.compact) div.right button.ignore-btn
{
    color: #a10000;
}
#trade .results:not(.compact) .resultset span.pull-left.stock
{
    width: 100%;
    text-align: left;
    font-weight: bolder;
}
#trade .results:not(.compact) .resultset span.pull-right
{
    /* float: none !important; */
}
#trade .results:not(.compact) div.right .btns
{
    line-height: 2.1;
}
#trade .results:not(.compact) .status
{
    line-height: initial;
}
#trade .results:not(.compact) div.right .status
{
    /* will be update later */
}
#trade .results:not(.compact) button.copy
{
    bottom: auto;
    top: 8px;
    left: 38px;
}
#trade .results:not(.compact) button.searchBy
{
    bottom: auto;
    top: 8px;
    left: 68px;
}
/* .middle item layout */
#trade .results:not(.compact) div.middle
{
    display: grid;
    grid-template-columns: 50% 50%;
    border: 1px solid #886437;
    background: rgba(0, 0, 0, 0.8);
}
#trade .results:not(.compact) .resultset .row.exchange div.middle
{
    display: initial;
}
#trade .results:not(.compact) .resultset .row.exchange .contact
{
    grid-column-start: 3;
}
#trade .results:not(.compact) .resultset .row.exchange .contact .slider-middle textarea.form-control
{
    height: 64px;
}
#trade .results:not(.compact) .itemPopupContainer
{
    border-right: 1px solid #886437;
}
#trade .results:not(.compact) .row .middle .itemPopupAdditional .s span::before
{
    content: '';
}
/* .divinationCard */
#trade .results:not(.compact) .itemPopupContainer.divinationCard
{
    min-width: 100%;
    height: auto;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent
{
    background: none;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .cardFace
{
    display: none;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemHeader > .itemName
{
    font-family: 'FontinSmallCaps', Verdana, Arial, Helvetica, sans-serif;
    font-weight: 400;
    font-size: 17.1px;
    font-size: 100% !important;
    color: #c8c8c8;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent
{
    padding-bottom: 7.15px;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .stackSize,
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .explicitModsWrapper
{
    position: unset;
    width: 100%;
    margin: 0;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .stackSize
{
    text-align: center;
    color: white;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .stackSize::before
{
    content: 'Stack Size: ';
    color: #7f7f7f;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .explicitModsWrapper
{
    font-size: 14.3px;
    height: 0%;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .explicitModsContainer
{
    height: 100%;
}
#trade .results:not(.compact) .itemPopupContainer.divinationCard .itemBoxContent .flavourTextWrapper
{
    display: none;
}
/* .gemPopup */
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > div:not(.vaalHeader):not(.requirements):not(.additionalProperty):not(.unmet),
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > .displayProperty > span:not([data-field=gem_level]):not([data-field=quality])
{
    display: none;
}
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > .displayProperty:nth-child(2),
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > .displayProperty:nth-child(6),
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > .requirements + .separator,
#trade .results:not(.compact) .itemPopupContainer.gemPopup .content > .vaalHeader + .separator
{
    display: inherit !important;
}
/* .explicitMod */
#trade .results:not(.compact) .itemPopupContainer .content .explicitMod > .lc.l > .d,
#trade .results:not(.compact) .itemPopupContainer .content .explicitMod > .lc.r > .d
{
    background-color: #0f304d;
}
#trade .results:not(.compact) .itemPopupContainer .content .explicitMod > .lc.l,
#trade .results:not(.compact) .itemPopupContainer .content .explicitMod > .lc.r
{
    z-index: 2;
}
#trade .results:not(.compact) .itemPopupAdditional
{
    margin-top: 0;
    font-size: 110%;
    height: 39px;
    padding-top: 39px;
    padding-left: 5px;
    background: none;
}
#trade .results:not(.compact) .itemPopupAdditional > span
{
    display: inline-grid;
}
#trade .results:not(.compact) .itemName,
#trade .results:not(.compact) .displayProperty,
#trade .results:not(.compact) .requirements
{
    display: inline-block;
}
#trade .results:not(.compact) .itemHeader,
#trade .results:not(.compact) .itemHeader > span.l,
#trade .results:not(.compact) .itemHeader > span.r
{
    background-image: none;
}
#trade .results:not(.compact) .itemName > span
{
    padding-left: 0;
    padding-right: 0;
    font-size: 90%;
}
#trade .results:not(.compact) .itemHeader
{
    height: 33px;
    white-space: nowrap;
    /* overflow-y: hidden; */
    padding-top: 5.18323px;
}
}
