css:
@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/.*") {
/* 通用设置 */
/* 背景图片，适用于非iframe页面 */

body.mainbody {
    background-color: transparent !important;
    background: url("https://i.imgur.com/ykBWOqM.png") no-repeat center top fixed !important;
    background-size: cover !important;
}
/* 顶部导航颜色 蓝色浙变 */

#menubar {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(46, 128, 209, 0.4)), to(rgba(35, 89, 148, 0.8)))!important;
}
/* 标头颜色 */

#online .bm_h,
.titlebar,
.forumhot .title_bar {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.30)), to(rgba(112, 112, 112, 0.60))) !important;
    opacity: 1.0 !important;
}
/* 适用于暗色背景，不适合亮色背景 */
/* 页面顶部个人信息 */

#submenu,
#submenu a {
    background: rgba(255, 255, 255, 0.1)!important;
    color: #FFFFFF !important;
}
/* 当前页面位置导航条 */

.pageinfo,
.pageinfo a {
    background: rgba(255, 255, 255, 0.1)!important;
    color: rgba(222, 222, 222, 1) !important;
}

#nav {
    border-color: grey!important;
}
/* 我的中心 下拉菜单 */

.sel_my {
    background: rgba(187, 195, 202, 0.0)!important;
}
/* 右上角用户信息区及导航栏下方区域 */

#um,
#um a,
.announcement.cl,
.announcement.cl a,
.topic_num.cl {
    color: #DDDDDD !important;
}
/* 右上角用户积分区 */

#extcreditmenu_menu a {
    color: #000000 !important;
}
/* END OF 适用于暗色背景，不适合亮色背景 */
/* 与菜单相关的部分链接字体需要反色 */

#menu a,
#mymenu {
    color: #FFFFFF !important;
    opacity: 0.8 !important;
}
/* 内容块背景色 */
/* 主体背景 */

.main {
    background: rgba(187, 195, 202, 0.5)!important;
    border-color: grey!important;
}
/* 选项卡选中项 */

.tab_forumhot li.current a {
    background: rgba(187, 195, 202, 0.5)!important;
    border-color: grey!important;
}
/* 弹出下拉菜单 */

.p_pop,
.popupmenu_popup {
    background: rgba(187, 195, 202, 0.8)!important;
    border-color: grey!important;
}
/* 输入区 */

/* input {
    background-color: rgba(187, 195, 202, 0.5)!important;
    color: #000000!important;
} */
/* 搜索图标 */

.btnsearch {
    opacity: 0.5 !important;
    padding: 6px !important;
}
/* 头像 */

.avt img {
    background: rgba(187, 195, 202, 0.5)!important;
    border-color: grey!important;
}
/* 签到 */

.fgbtcheckintoday,
.fgbtcheckin {
    background: rgba(187, 195, 202, 0.5)!important;
    border-color: grey!important;
}
/* 友链 在线用户 */

#forumlink,
#online {
    background: rgba(187, 195, 202, 0.5)!important;
    border-color: grey!important;
}
/* 最新帖子内容概要 */

.i_hot dd {
    color: #000000 !important;
}
/* 链接默认为黑色字体 */

a {
    color: #000000 !important;
    opacity: 1.0 !important;
    font-weight: 500 !important;
}
/* 图片透明度 */

img,
#imgADPlayer {
    opacity: 0.75 !important;
}
/* 按钮 */

button.pn {
    background-color: rgba(187, 195, 202, 0.5)!important;
    color: #000000 !important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/.*") {
/* 格式替换 */
/* 文本内引用 */

.quote {
    background-color: rgba(187, 195, 202, 0.5)!important;
}

.hide {
    background-color: rgba(187, 195, 202, 0.5)!important;
}

.blockcode {
    background: rgba(187, 195, 202, 0.5) url(http://buaabt.cn/templates/default/images/codebg.gif) repeat-y 0 0!important;
}

.blockcode ol li:hover {
    background-color: rgba(187, 195, 202, 0.8)!important;
}
/* 用户内容 帮助等 */

.wrap.uc {
    background: rgba(187, 195, 202, 0.4)!important;
    border: 1px solid grey !important;
}

.uc_app {
    background: rgba(187, 195, 202, 0.6)!important;
}

.uc_app li.current {
    background: rgba(187, 195, 202, 0.5)!important;
}

.solidline {
    border-bottom-width: 1px!important;
    border-color: rgba(112, 112, 112, 0.2)!important;
}
/* 提示信息 表头 */

.hintinfo,
.colplural {
    background: rgba(187, 195, 202, 0.5)!important;
}

.f_tab a {
    background: rgba(187, 195, 202, 0.5)!important;
}

.f_tab .cur_tab a {
    background: rgba(187, 195, 202, 0.8)!important;
}

.notefilter {
    background: rgba(187, 195, 202, 0.8)!important;
}
/* 文本输入区 */

.sipt,
textarea,
.txt {
    background-color: rgba(187, 195, 202, 0.5)!important;
}

.ftid a {
    background: url(http://buaabt.cn/editor/images/newarow.gif) no-repeat scroll 100% 0 rgba(187, 195, 202, 0.5)!important;
}
/* 可编辑区表头 */

.editor_tb {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.30)), to(rgba(112, 112, 112, 0.60))) !important;
}
/* 下拉菜单 */

.sltm {
    background-color: rgba(187, 195, 202, 0.6)!important;
}

.sltm li {
    color: #000000 !important;
}

.m_c {
    background: rgba(187, 195, 202, 0.8)!important;
}

/* 消息提醒 */
.crly {
    border: grey!important;
    background: rgba(187, 195, 202, 0.8)!important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/(showseeds|showforum|showtopic).*") {
/* 表头 分隔栏 */

.category,
.separation {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.30)), to(rgba(112, 112, 112, 0.60))) !important;
}

.threadlist th,
.threadlist td {
    border-color: grey!important;
}
/* 快速发布 表头 */

#quickpost .bm_h {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.30)), to(rgba(112, 112, 112, 0.60))) !important;
}
/* 分类按钮 */

#headfilter {
    border-top: 0px !important;
}

#headfilter a {
    background-color: rgba(187, 195, 202, 0.5)!important;
}

#headfilter a.current,
#headfilter a:hover {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.90))) !important;
}
/* 被选中的高亮项 */

li a.current {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.90))) !important;
}
/* 底部边框 */

.smiliesgroup ul {
    border-bottom: 1px solid grey !important;
}
/* 页码区 */

.pages a,
.pages span,
.pages kbd,
.pages kbd input {
    background-color: rgba(187, 195, 202, 0.5)!important;
}
/* 特殊类型高亮 */

tr.PrivateBTSeedListUpload td {
    background-color: rgba(151, 180, 206, 0.7)!important;
}

tr.PrivateBTSeedListPublish td {
    background-color: rgba(224, 224, 224, 0.7) !important;
}

tr.PrivateBTSeedListFinished td {
    background-color: rgba(224, 224, 255, 0.7) !important;
}
/* 种子条目 */

tr.PrivateBTSeedListTop td,
tr.PrivateBTSeedList td {
    background-color: rgba(187, 195, 202, 0.5)!important;
}

.PrivateBTSeedListGrayL,
.PrivateBTSeedListGrayR {
    color: #000000;
}
/* 鼠标悬停于条目上方 */

.onthread,
tr.PrivateBTSeedListFinished:hover td,
tr.PrivateBTSeedListUpload:hover td,
tr.PrivateBTSeedListTop:hover td,
tr.PrivateBTSeedList:hover td {
    background-color: rgba(187, 195, 202, 0)!important;
}
/* 下一页按钮 */

.big_nextbtn {
    border: grey !important;
    background-color: rgba(187, 195, 202, 0.5)!important;
}

.big_nextbtn:hover {
    background-color: rgba(187, 195, 202, 0)!important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/showtopic.*") {
/* 作者信息区 */

.postauthor {
    background-color: rgba(187, 195, 202, 0.5)!important;
    border-right: 0px !important;
}
/* 楼层分隔 */

.threadad .postauthor,
.adcontent {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.30)), to(rgba(112, 112, 112, 0.60))) !important;
}
/* 签名档最大高度 */

.signatures {
    max-height: 300px !important;
}
/* 种子信息区 */

.PrivateBTSeedInfoTable {
    border-color: rgba(128, 128, 128, 0.24) !important;
}

tr.PrivateBTSeedInfoListDownLink td,
tr.PrivateBTSeedInfoList td {
    border-top: 1px solid grey !important;
}

.PrivateBTSeedInfoListTitle,
tr.PrivateBTSeedInfoListTitle td {
    background: rgba(187, 195, 202, 0.6)!important;
    color: #da5d0a!important;
}

tr.PrivateBTSeedInfoListDownLink {
    background: rgba(187, 195, 202, 0.6)!important;
}

tr.PrivateBTSeedInfoList {
    background: rgba(187, 195, 202, 0.6)!important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/(edit|publish|posttopic|postreply).*") {
/* 主体背景 */

.PrivateBTexfm,
.pbt,
.exfm,
#e_body,
#calendardiv {
    background-color: rgba(187, 195, 202, 0.5)!important;
    color: #000000 !important;
    border: 1px dashed grey !important;
}

table.PublishTable .PublishWarning {
    border-bottom: 1px dashed grey !important;
}
/* 可点击标签及分类标签 */

a.PublishTag,
.f_tab a {
    background-color: rgba(187, 195, 202, 0.5)!important;
    color: #000000 !important;
}
/* 选中的分类标签 */

.cur_tab {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.80))) !important;
    color: #000000!important;
}
/* 输入区及按钮 */

input.file_btn,
td input,
.pbn input,
.p_opt input{
    background-color: rgba(187, 195, 202, 0.5)!important;
    color: #000000 !important;
}
/* 表头 */

#rstnotice,
#e_controls,
#e_bbar {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.80))) !important;
    color: #000000!important;
}
/* 编辑区按钮 */
/* 按钮分隔栏 */

.edt .b1r,
.edt .b2r {
    border-left: 1px solid grey !important;
    border-right: 1px solid grey !important;
}
/* 功能按钮 */

#e_simple,
#e_fullswitcher {
    background: rgba(222, 222, 222, 0.5)!important;
    color: #000000 !important;
}

#e_button a {
    border: 1px solid grey !important;
}

#e_button a.hover,
#e_button a:hover {
    background-color: rgba(222, 222, 222, 0.5)!important;
}
/* 字体字号选择 */

.edt .b2r a.dp {
    background: url(http://buaabt.cn/editor/images/newarow.gif) no-repeat scroll 100% 0 rgba(187, 195, 202, 0.5)!important;
}
/* 弹出框 */

.p_pof,
.p_pof textarea {
    background-color: rgba(187, 195, 202, 0.6)!important;
    color: #000000 !important;
}

.p_pof a.current {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.80))) !important;
}
/* 编辑区iframe */

iframe,
body.iframebody,
.area.cl,
#e_textarea {
    background-color: rgba(255, 255, 255, 0)!important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/(showseeds|showforum|showtopic|edit|publish|posttopic|postreply).*") {
/* 表情区 */

div.PrivateBTQuickSmilesShowDiv,
.smiliesgroup ul,
.smiliesgroup li a {
    border: 1px solid grey!important;
}

.smiliesgroup li a.current {
    background: -webkit-gradient(linear, left top, left bottom, from(rgba(153, 153, 153, 0.70)), to(rgba(112, 112, 112, 0.80))) !important;
}
}

@-moz-document regexp("https?://(.*buaabt|buaabt-cn\\.e\\.buaa\\.edu|bt\\.buaa\\.edu)\\.cn/search.*") {
/* 搜索页 */

.subhead,
.subhead a {
    background: rgba(255, 255, 255, 0.1)!important;
    color: #EEEEEE !important;
}

.sttl,
#tpsch .tb a,
#tps_form,
#tps_form td,
button,
select {
    background-color: rgba(187, 195, 202, 0.5)!important;
    border-color: grey
}
}
