@-moz-document domain('wikia.com'), domain("fandom.com") {
/* Make wikia nav bar attach to the top of the page */
#globalNavigation {
    position: relative;
    z-index: 100;
    box-shadow: none
}

/* Right Bar*/
#WikiaRailWrapper {
	display: none;
}

#WikiaMainContent {
    width: 100%
}

/* Bar at bottom */
#WikiaBar {
    display: none;
}

/* Advertisement at the top */
.WikiaTopAds {
    display: none;
}

