css:
@-moz-document domain("kshow123.net")
{
#content a {
    color: #fff;
}
.sound {
    display: none
}
.pagination li a {
    color: grey !important;
}
.jw-background-color {
    background: rgb(22, 22, 22);
}
#player-wrapper {
    margin-top: -20px;
    padding: 0 10%;
    background-color: rgba(0, 0, 0, 0.4);
}
.btn-default {
    color: #fff;
    background-color: #151515;
    border-color: #151515;
}
.btn-group.open .dropdown-toggle {
    -webkit-box-shadow: inset 0 3px 5px rgba(0, 0, 0, .125);
    box-shadow: inset 0 3px 5px rgba(0, 0, 0, .125);
    background-color: #151515;
    color: #fff;
    border: 0px;
}
#content .row table tbody td a {
    color: #fff;
}
#recent,
#featured,
#rated,
#random,
#info,
#list-episodes,
#list-episodes,
#player,
#disqus_thread {
    background: #0e0e0e !important;
    padding: 10px;
    margin-bottom: 10px;
}
#sidebar {
    background: #0e0e0e !important;
    padding: 5px;
}
#content .page-header h3 {
    font-family: Arial;
    font-size: 16px;
    border-bottom: 2px solid #fff;
    color: #fff;
    display: inline;
    padding-bottom: 10px;
}
#geniee_overray_base {
    display: none
}
#sidebar {
    background: #0e0e0e !important;
    padding: 15px;
}
#header .navbar-default .navbar-nav li a {
    color: #fff;
    font-size: 16px;
    font-weight: bold;
}
#content .alert-info {
    background-color: #0e0e0e !important;
    border: #0e0e0e !important;
}
.alert {
    color: #2a2a2a;
}
#content .breadcrumb {
    padding: 10px 15px;
    margin-bottom: -20px;
    list-style: none;
    background-color: #0e0e0e !important;
    border-radius: 0;
}
.jw-logo-top-left {
    display: none;
    top: 0;
    left: 0;
}
.server_item strong {
 	color: #aaa !important;   
}
.onesignal-bell-container {
    display: none;
}
#player > .text-muted.small {
    display: none;
}
.jStar {
    background: url("https://i.imgur.com/QPxbV54.png") !important ;
}
}
