css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("arche.univ-lorraine.fr") {
    .navbar-light .navbar-brand {
        background-color: #040440;
    }
    
    header.navbar {
        background-color: #040440;
        border-color : #040440;
    }
    
    #nav-drawer .list-group .font-weight-bold {
        background-color: #040440;
        border: 1px solid #040440;
    }
    
    #nav-drawer, #page-wrapper {
        background-image: url("data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==") !important;
        background-repeat: repeat;
        background-color: #181818;
    }
    
    #nav-drawer .list-group .font-weight-bold {
        font-size: 18px;
        background-color: #040440 !important;
        border: 1px solid #040440 !important;
    }
    
    .list-group-item {
        background-image: url(data:image/png;base64,) !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
        color : white;
        font-weight: bold;
    }
    
    .card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .forumpost, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-right, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-bottom, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax #chat-messages .chat-message.course-theme, .well {
   
        background-image: url(data:image/png;base64,) !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
        color :white;
    }
    
    .card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .forumpost, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-right, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax .yui-layout-unit-bottom, .yui-skin-sam .yui-layout.path-mod-chat-gui_ajax #chat-messages .chat-message.course-theme, .well {
        border: 1px solid #121212;
    }
    
    #page-footer {
        background-color: #040440 !important;
        border-top: 1px solid #ddd;
        filter: brightness(50%);
    }
    
    .table-striped tbody tr:nth-of-type(2n+1), table.collection tbody tr:nth-of-type(2n+1), table#explaincaps tbody tr:nth-of-type(2n+1), table#defineroletable tbody tr:nth-of-type(2n+1), table.grading-report tbody tr:nth-of-type(2n+1), table#listdirectories tbody tr:nth-of-type(2n+1), table.rolecaps tbody tr:nth-of-type(2n+1), table.userenrolment tbody tr:nth-of-type(2n+1), form#movecourses table tbody tr:nth-of-type(2n+1), #page-admin-course-index .editcourse tbody tr:nth-of-type(2n+1), .forumheaderlist tbody tr:nth-of-type(2n+1), table.flexible tbody tr:nth-of-type(2n+1), .generaltable tbody tr:nth-of-type(2n+1){
        background-image: url(data:image/png;base64,) !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
        color :white;
        border: none !important;
    }
    
    .generaltable * {
        background-image: url(data:image/png;base64,) !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
        color :white !important;    
        border: none !important;
    }
    
    .generaltable tr:hover{
        background-color: pink;
    }
    
    .path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded {
        background-image: url(data:image/png;base64,) !important;
        background-repeat: repeat !important;
        background-color: rgba(0,0,0,.15) !important;
        border: 1px solid #111 !important;
        border-bottom: 1px solid rgba(255,255,255,.16) !important;
        box-shadow: inset 0 0px 3px 0 #111 !important;
        color :white;    
        border: none !important;
    }
    
}
