css:
@-moz-document domain(lark.alipay.com) {
  .lark-table,
  .bi-unstyle .bi-table, .bi-typo .bi-table {
    max-width: calc(100vw - 400px);
    min-width: 100%;
  }
  .lark-table table,
  .bi-unstyle .bi-table, .bi-typo .bi-table table {
    width: auto;
    min-width: 100%;
    display: inline-block;
    max-width: calc(100vw - 420px);
  }
}
