css:
@-moz-document domain("cslab.ece.ntua.gr")
{
body { 
	background-color:black; color:grey; 
	font-size:13px; 
	font-family:Verdana, Tahoma, Lucida, Helvetica, 'Sans Serif'; 
}

h1 {
	font-size: 15px;
	margin-left: 10px;
}

h2 {
	font-size: 14px;
	margin-left: 12px;
}

h3 {
	font-size: 13px;
	margin-left: 14px;
}

li h1 {
	font-size: 13px;
	font-weight: bold;
	padding: 0px;
	margin: 5px;
}

p {
    margin-left: 10px;
    margin-right: 10px;
    text-indent: 10px;
    text-align: justify;
}

a:link, a:visited { 
	color:#8C0707; 
	font-weight: bold;
}

a:hover, a:active {
	color: #0a0f7e;
}


table {
	/* border: 2px solid black; */
	border: 1px solid black;
	border-spacing:0;
	border-collapse: collapse;
	font-size: 14px;
	margin-left:10px;
}
	

td, th {
	border: 1px dotted #b0b0b0;
	padding: .5em;
}

tr:hover {
	background: #f0f0f0;
}



#main {
	margin-left: 190px;
	margin-right: 190px;
	padding-bottom: 15px;
	padding-left: 5px;
	padding-right: 5px;
	border: 2px solid #8C0707;
	-moz-border-radius: 5px;
}


#header {
	padding: 5px;
	margin-bottom: 10px;
}

.mainheader {
	border: 2px solid #8C0707;
	-moz-border-radius: 5px;
}

.mainheader h1 {
	font-variant: small-caps;
	text-align: center;
	font-family: Helvetica;
	font-size:25px;
	padding: 0px;
	margin: 0px;
}

.commonheader  {
	border: 2px solid #8C0707;
	-moz-border-radius: 5px;
}

.commonheader h1 {
	font-variant: small-caps;
	text-align: left;
	font-family: Helvetica;
	font-size:25px;
	padding: 0px;
	margin: 0px;
}

.commonheader ul {
	white-space: nowrap;
	text-align: right;
	padding : 0;
	margin-top: 0;
	margin-bottom: 0;
	margin-right: 10px;
}

.commonheader li {
	display : inline;
	border-right:  black;
	margin-right: 3px;
}

.commonheader li:before { content: "::"; }

#leftcolumn {
	width: 190;
	float: left;
}

#rightcolumn {
	width: 190;
	float: right
}

#footer{
	clear: both;
	text-align: center;
	padding: 5px;
	font-size: 10px;
	margin-top: 10px;
}

#footer p {
	margin: 0px;
	padding: 0px;
	text-align: center;
}

.box {
	border: 2px solid #8C0707;
	-moz-border-radius: 5px;
	margin-bottom: 6px;
	margin-left: 6px;
	margin-right: 6px;
	padding: 2px;
	text-align: left;
}

.box a {
	font-weight: normal;
}

.box ul {
	margin-left: 5px;
	margin-top: 5px;
	padding: 0px;
}

.box li {
	list-style-type: none;
	padding: 6px;
}

.box li:hover {
	background: #f0f0f0;
}

.box ul h1 {
	text-align: left;
	font-size: 14px;
	text-decoration: none;
	margin-top: 2px;
}

.box h1 {
	margin: 0px;
	text-decoration: underline;
	padding: 0px;
	font-size: 14px;
	font-variant: small-caps;
	text-align: center;
}

.facilities h1 {
	margin-left: 5px;
	margin-right: 5px;
	font-size: 15px;
	margin-bottom: 15px;
	border-bottom: 3px solid #dddddd;
}

.facilities ul {
	padding: 0px;
	margin-left: 0px;
	margin-right: 0px;
	margin-bottom: 0px;
	margin-top: 0px;
}

.facilities li {
	background-color: #dddddd;	
	padding-top: 0px;
	padding-bottom: 5px;
	padding-left: 5px;
	padding-right: 5px;
	margin-left: 5px;
	margin-right: 5px;
	margin-top: 8px;
	margin-bottom: 8px;
	border: 1px solid black;
	list-style-type: none;
	text-align: justify;
}

.facilities li h1 {
	font-size: 13px;
	padding: 0px;
	margin: 0px;
	border-bottom: 1px solid black;
}

.facilities li p {
	margin: 0px;
	padding: 2px;
}

.facilities li li {
	margin-left: 18px;
	margin-right: 0px;
	margin-bottom: 0px;
	margin-top: 0px;
	padding: 0px;
	border: 0px;
	list-style-type: disc;
}

.staff ul {
	padding: 0px;
	margin: 0px;
}

.staff ul h1 {
	padding: 1px;
	margin: 1px;
	font-size: 13px;
	border-bottom: 1px solid black;
}

.staff li {
	padding-top: 0px;
	padding-bottom: 10px;
	padding-left: 2px;
	padding-right: 5px;
	margin-left: 5px;
	margin-right: 10px;
	margin-top: 3px;
	margin-bottom: 3px;
	list-style-type: none;
	text-align: justify;
}


.staff li li {
	margin-left: 20px;
	margin-right: 0px;
	margin-bottom: 0px;
	margin-top: 0px;
	padding: 0px;
	border: 0px;
	list-style-type: disc;
}

.courses table col:first-child {
	width: 300px;
}

#coursemenu ul {
	margin-left: 0px;
	margin-bottom: 0px;
}

ul.announcements {
	margin-left: 0px;
	padding-left: 20px;
	border: 0px;
}

ul.announcements li {
	list-style-type: none;
	margin-left: 0px;
}

#coursemenu  li {
	list-style-type: none;
	padding: 10px;
	height: 30px;
	border-top: 1px dashed #dddddd
}

#coursemenu li:first-child {
	border-top: 0px;
}


#coursemenu img {
	float: left;
	border: 0;
}

#coursemenu li:hover {
	background:white;
}

.course h2 {
	border-bottom: 1px solid #dddddd;
	margin-right: 10px;
}

img.mail {
	margin: 0px;
}

table.person {
	margin-right: 0px;
	padding: 0px;
	margin-bottom: 10px;
	border: none;
}

table.person td  {
	border: none;
	text-align: left;
	margin: 0px;
	padding: 0px;
}

table.person th  {
	border: none;
	text-align: left;
	margin: 0px;
	padding: 0px;
}

table.person tr:hover {
	background:inherit;
}

p.note {
	font-style: italic;
	font-size: 12px;
}

.publications h2 {
	border-bottom: 1px solid #dddddd;
	margin-right: 10px;
}

.publications ul.years {
	text-align: center;
	padding-left:0px;
	padding-right:0px;
	padding-top:10px;
	padding-bottom:0px;
	margin: 0;
}

.publications ul.years li {
	display : inline;
	margin-right: 3px;
}

.publications li.publication {
	display: block;
	list-style-type: none;
	margin-right: 10px;
	text-align: left;
	padding: 5px;
	margin-top: 15px;
	margin-bottom: 15px;
}

.publications li.publication:hover {
	background: #f0f0f0;
}

.publications li.publication .title {
	font-weight: bold;
}

.publications li.publication .authors {
	font-style: italic;
}

.publications li.publication .links ul {
	padding: 0px;
	margin: 0px;
	text-align: left;
}

.publications li.publication .links li {
	display: inline;
	padding: 0px;
	margin-top: 0px;
	margin-bottom: 0px;
	margin-left: 0px;
	margin-right: 2px;
}

table.notes {
	border:  1px solid black;
}

table.notes th {
	border:  0px;
	border-bottom: 1px solid black;
	border-right: 1px solid black;
}

table.notes td {
	border:  0px;
	border-right: 1px solid black;
}

tr.hl {
	background:#ffffdd;
}

tr.hl:hover {
	background: #ffffaa;
}

tr.hl2 {
	background:#ddffdd;
}

tr.hl2:hover {
	background:#ddffaa;
}

tr.hl3 {
	background:#ffdddd;
}

tr.hl3:hover {
	background:#ffaaaa;
}
}
