css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dexless.com") {
  
body {
    background-color: #383838;  
    color: #D6D6D6;
}
    
/****** Main/Forum page background color changes ******/    
    
.sectionMain {
    background-color: #434343;
}
    
    
.node .nodeLastPost .lastThreadTitle a {
    color: #BABABA;
    font-weight: bold;
}    
    
.node .nodeLastPost {
    border-bottom: 1px solid rgb(242, 242, 242);
        border-bottom-style: solid;
    border-bottom-style: none;
    border-left: 1px solid #383838;    
}    

.threadListItem {
    border-top: 1px solid #383838;    
}
    
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: #434343;
    border-color: #383838;
}
  
.node .unread .nodeIcon.hasGlyph {
    color: #eeaa21;
    background-color: #383838;  
}
  
.node .nodeIcon.hasGlyph {
    text-align: center;
    background-color: transparent;
    background: none;
        background-color: #383838;
        background-image: none;
    font-size: 16px;
    color: rgb(197, 197, 197);
    background-color: #383838;
    border: 1px solid #525252;  
}
  
.nodeList .categoryStrip {
    border-top: 2px solid #eeaa21;
    border-bottom: 1px solid #434343;    
}
  
.node.level_2 .nodeInfo::before {
    background-color: #383838;    
}  

.secondaryContent {
    background-color: #434343;   
    border-bottom: 1px solid #525252;
}    
    
/* Messages/Likes/Trophy Main page sidebar */    
    
.visitorPanel .stats dl {
    background: #383838;
}    

.primaryContent {
    border-bottom: 1px solid #525252;    
}    
    
.Menu {
    background-color: #434343;
    font-size: 11px;
    background-color: #434343;
    margin-top: 10px;
    border-radius: 5px;
    overflow: hidden;
    box-shadow: 1px 2px 5px rgba(0,0,0, 0.2);
    min-width: 200px;
    width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}    
    
.Menu .primaryContent {
    background-color: rgba(67, 67, 67, 0.96);
}    

.Menu .blockLinksList {
    background-color: #434343;
}    

.Menu .secondaryContent {
    background-color: rgba(61, 61, 61, 0.96);
} 
    
.Menu .sectionFooter {
    background-color: rgba(61, 61, 61, 0.9);
}    
    
.blockLinksList a, .blockLinksList label {
    color: rgb(98, 98, 98);
    padding: 10px 14px;
    border-style: none;
        border-top-style: none;
    border-color: #525252;
    border-top-width: 1px;
    border-top-style: solid;
}
    
.blockLinksList a:hover {
    background-color: #383838;
}
    
#AccountMenu .menuColumns .col1 {
    border-right: solid 1px #525252;
}    

/* Arrow for pop-up */    
    
.Popup .PopupControl.PopupOpen::after, .Popup.PopupContainerControl.PopupOpen::after {
    content: '';
    position: absolute;
    margin-left: -7px;
    left: 50%;
    border-style: solid;
    border-width: 0 7px 6px 7px;
    border-color: transparent transparent #434343 transparent;
    bottom: -10px;
}
    
/* GMS Time Background (commented because idk how to change this shit) 
    
#c1 {
    background-color: transparent !important;

}
   
    */
/***** Main Page/General text color changes  (LINKS ARE #BABABA AND TEXT IS #D6D6D6) ******/    
    
.node .nodeText .nodeTitle {
    color: #BABABA;     
}
    
a:link, a:visited {
    color: #B3B3B3;
}    
    
a:link {
     color: #D6D6D6;
}    
    
a[href]:hover {
		color: #D6D6D6; 
}
.node .nodeLastPost .lastThreadTitle a {
    color: #BABABA;
    font-weight: bold;
}        
    
.uix_nodeTitle_status {
    background-color: #383838;
    color: #BABABA;
}    
    
.breadcrumb .crust a.crumb {
    color: #D6D6D6;    
}    
    
.sidebar .visitorPanel .username {
    color: #BABABA;    
} 

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    color: #BABABA;    
}

/* Dailies */    
    
.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a {
    color: #BABABA;    
}
 
/* User List Usernames */    
    
.sidebar .userList .username {
    color: #BABABA;    
}
    
/********* In-thread Style ********/
    
/* Main post colors + User info */    
    
.messageList .message {
    background-color: #434343;
    box-shadow: 1px 2px 3px rgba(0,0,0,.1);    
}
    
.message .messageDetails {
    border-bottom: 1px solid #525252;    
}  
    
.message .signature {
    font-size: 9pt;
    color: rgb(144, 144, 144);
    padding: 15px 0;
    margin-top: 15px;
    margin-bottom: 15px;
    border: 0 solid #525252;    
        border-top-width: 0px;
    border-bottom-width: 0px;
border-top-width: 1px;
border-bottom-width: 1px;
}    
 
.messageUserInfo {
    background-color: #434343;
    border: 0 solid #525252;
        border-right-width: 0px;
    border-right-width: 1px;    
}    
    
.messageUserBlock a.username {
    color: #BABABA;    
}    
    
/* Likes */
   
.message .likesSummary {
    color: #BABABA;
    background-color: #434343;
}    
    
/* Quotes */
    
.bbCodeQuote .attribution {
    border-left: 3px solid #525252;    
}
    
.bbCodeBlock .type {
    color: #BABABA;
    background-color: #525252;
}
    
.bbCodeQuote .quoteContainer {
    background-color: #383838;
    border-left: 3px solid #525252;    
}
    
/* "Users viewing this thread */
    
#uaThreadViewContainer h3, #uaThreadReadContainer h3 {
    color: #D6D6D6;
    border-bottom: 1px solid #525252;    
}
    
#uaThreadViewContainer h3 .footnote, #uaThreadReadContainer h3 .footnote {
    color: #BABABA;
}   
    
/* Similar Threads */
    
.discussionList .sectionHeaders {
    font-weight: bold;
    font-size: 15px;
    color: rgb(66, 66, 66);
}    
    
.discussionList .sectionHeaders {
    color: #D6D6D6;
    background-color: #5E5E5E;
    border: 0 solid #525252;
        border-top-width: 0px;
    border-top-width: 2px;   
}    
    
.discussionListItem {
    background-color: #434343;
    border-bottom: 1px solid #525252;
}   
    
.discussionListItem .stats dl {
    padding: 0 20px;
    border-left: 1px solid #525252;
    border-right: 1px solid #525252;
}    

.discussionListItem:nth-child(2n) {
    background-color: #484848;
}    
    
/* Reply box */    
    
.quickReply {
    background-color: #434343;
    border-style: none;
        border-top-style: none;
    border-top: 2px solid #eeaa21;
    border-radius: 1px;
    box-shadow: 1px 2px 3px rgba(0,0,0,.1);    
}
    
.textCtrl {
    color: #D6D6D6;
    background-color: transparent;
    padding: 8px;
    margin-bottom: 4px;
    border: 0px none #525252;
        border-bottom-width: 0px;
        border-bottom-style: none;
        border-top-color: #525252;
        border-right-color: #525252;
        border-bottom-color: #525252;
        border-left-color: #525252;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    border-radius: 0;
    outline: 0;
    box-sizing: border-box;
    min-height: 36px;    
}    
    
/* Thread subsection list */
    
.discussionList .sectionHeaders a {
    color: #BABABA;
}    
    
.sectionFooter {
    color: #D6D6D6;
    background-color: #434343;
    border-top: 1px solid #525252;
}   
    
#DiscussionListOptionsHandle a {
    color: #BABABA;
    background-color: #434343;
    border: 1px solid transparent;
        border-top-width: 1px;
        border-top-style: solid;
        border-top-color: transparent;
    border-top: 1px none black;
    border-bottom-right-radius: 1px;
    border-bottom-left-radius: 1px;
    box-shadow: 1px 2px 2px rgba(0,0,0,0.1);    
}    
 
    
/* Q&A Threads */
    
.bestAnswer.miniDisplay {
    background-color: #3A4537;
    padding: 6px;
    margin-right: auto;
    margin-left: auto;
    border: 1px dotted #525252;
    box-sizing: border-box;
    width: 100%;
    height: auto;
}    
    
.baThreadSummary {
    font-size: 10pt;
    background-color: #3A4537;
    background-position: top;
    padding: 10px;
    margin: 0 0 15px;
    border: 1px solid #525252;
    border-radius: 5px;
}    
    
    
    
/* Tooltip on hover */
    
.xenPreviewTooltip {
	background-color: #383838;
    border: 1px solid #525252;
    border-radius: 1px;
}
    
	.xenPreviewTooltip .arrow
	{
		border-top: 15px solid #383838;
        border-right: 15px solid transparent;
        border-bottom: 1px none black;
        border-left: 15px solid transparent;

		_display: none;
	}
		.xenPreviewTooltip .arrow span
		{
			border-top: 15px solid #383838;
            border-right: 15px solid transparent;
            border-bottom: 1px none black;
            border-left: 15px solid transparent;
}    
    
    
/* Polls */
    
.pollBlock .question .questionText {
    color: #D6D6D6;
}    

.pollBlock .pollOption label:hover {
    background: #525252;
}    

.pollBlock .questionMark {
    background: #525252;
    color: #BABABA;
}    
    
/*********** User Profile ***********/
    
.profilePage .tabs.mainTabs {
    background-color: #434343;
    border-bottom: solid 1px #525252;
}    
    
.tabs li a, .tabs.noLinks li {
    color: #D6D6D6;
    text-decoration: none;
    background-color: #434343;
}    
    
.profilePage .tabs.mainTabs li a:hover {
    background-color: #383838;
    color: #D6D6D6
}    
    
.tabs li a::after, .tabs.noLinks li::after {
    background-color: #525252;
}    
    
.profilePage .tabs.mainTabs li.active a:hover {
    background-color: #eeaa21;
}    
    
/* Medals */
    
.subHeading {
    background-color: #434343 !important;
    border: 0px !important;
    color: #BABABA;
}    
    
.textWithCount.subHeading .text {
    color: #D6D6D6;
}    
    
.textWithCount.subHeading .count {
    border-radius: 5px;
    border: 1px solid #525252;
    background-color: #434343;
    color: #BABABA;    
}
    
.followBlocks .infoBlock .secondaryContent .sectionFooter {
    background-color: #434343 !important;
    border-top: 0px !important;
}    
    
.profilePage .mast .MedalsBlock .primaryContent.MedalHeap {
    border: 1px solid #434343;
        border-top-width: 1px;
        border-right-width: 1px;
        border-bottom-width: 1px;
        border-left-width: 1px;
    border-width: 0 1px;
} 
    
.primaryContent {
    background-color: #434343;  
}

.profilePage .primaryUserBlock .userStatus {
    color: #D6D6D6;
    background-color: #434343;
}    

#medals .sectionFooter {
    background-color: #434343 !important;
}
    
/* Comment box */
    
.profilePage .profilePoster {
    background-color: #434343;
}    
    
.messageSimple .poster {
    color: #BABABA;
}    
 
.messageSimple .likesSummary {
    color: #D6D6D6;
    background-color: #434343;
}    
  
/* Recent Activity */
    
.profilePage .profileContent:not(#profilePosts) {
    background-color: #434343;
    padding: 20px;
}    
    
    
/********* Media **********/    
    
.xengalleryContainer .insideContainer {
    background-color: #434343;

}    
    
.gridSection {
    background-color: #434343;
}    
    
.mediaContainer {
    border: 1px solid #525252;
    border-radius: 4px;
}
    
.mediaContainer .mediaControls {
    border-top: 1px solid #525252;
}    
    
.mediaLabel.labelStandard {
    color: #B6B6B6;
    background-color: #383838;
    border-color: #525252;
}    
    
.mediaLabels .mediaLabel {
    background: transparent;
}
    
.xengallerySideBarContainer .xengallerySideBar .section h3 {
    color: #D6D6D6;
    border-bottom: 1px solid #525252;    
}
    
/******* Guides ********/
    
    
.zineSrchHeaderBar .sectionMain, .zineCatHeaderBar .sectionMain {
    background-color: #434343;
}    
    
.zineSrchHeaderBar, .zineCatHeaderBar {
    border: 1px solid #525252;    
}
    
.zineTabHeader .zineHeadRight li {
    border-left: 1px solid #525252;
}    
    
.zineCell a:hover {
    background-color: #434343;
}    
    
.zineTabHeader h1 {
    color: #D6D6D6;
}    
    
.cateListFull .catItemPad {
    padding: 8px 10px 10px 10px;
    background: #434343;  
}
    
/* covering up the " ... " */    
    
.titleAndImage {
    color: #434343;
}    
    
.zineTabHeader .zineHeadLeft li {
    border-right: 1px solid #525252;
}    
    
.blockLink a, a.blockLink:hover {
    background-color: #525252;
}    
    
.articleList h3 {
    color: #BABABA;
}    
    
.articleList .titleContent {
    position: relative;
    border-bottom: 1px solid #525252;
    background-color: #434343;
    text-shadow: 0 1px #000;
    color: #BABABA;
}    
    
.articleList .titleContent:hover {    
    background-color: #525252;
}
    
.ratingContent, .authorContent, .articleSum {
    background: #434343;
}    
    
.articleList .styledBox .artImg img {
    border-bottom: 1px solid #525252;
}
    
    
/* Inside guides */
    
.resourceBox {
    background: #434343;
    border: 1px solid #525252;
}
    
.tabs li a:hover {
    background-color: #383838;   
    color: #BABABA;
}
    
.tabs li.active a, .tabs.noLinks li.active:hover {
    background-color: #eeaa21;
    color: #fff;
}    
    
.tagContent .tag {
    text-shadow: none;
    color: #434343;
}    
    
    
/********* Streams *********/
    
.EWRrio_Streams .sectionMain {
    background-color: #434343 !important;
}    
    
/********* Guilds *********/
    

   
.textHeading, .xenForm .sectionHeader {
    color: #D6D6D6;
    background-color: #434343 !important;  
}    
    
.TeamListSidebar h3 {
    color: #D6D6D6;
    border-bottom: 1px solid #525252;
}    
    
.TeamListSidebar > * {
    border: 1px solid #525252;
        border-bottom-width: 1px;
        border-bottom-style: solid;
        border-bottom-color: #525252;
    border-bottom: 0;
    border-radius: 3px;
}    
    
.TeamListMain .tabs {
    background-color: #434343;
}
    
.Team_index .TeamListMain .section {
    background: #434343 !important;
} 
    
.Team_ListCardItem .Team_ListCardBody {
    background-color: #434343;
}    
    
.Team_ListCardBody .mdl-card__supporting-text {
    color: #D6D6D6;
}
    
/* Featured icon */
    
.Team_BadgeList li:not(.Team_RibbonWrap) {
    color: #434343;
    background: #eeaa21;
    padding: 2px 5px;
    border: 1px solid #eeaa21;
}    
    
/* Inside Guilds */
    
.Team_navItem {
    background: #434343;    
}    
 
.Team_navItem selected {
    background: #434343;    
}

    
/****** Tools *******/
    
#pageNodeContent article div   {
    background: #434343 !important;
}
     
/* Dailies */
    
.bwalktxt {
    background: #383838 !important;
}    
 
.selectorbox {
    background: #383838 !important;    
}
    
/************** Button *************/
    
.button.primary {
    background-color: #434343;    
}   
    
.button.primary:hover {
    border-color: #eeaa21;
    color: #BABABA;
}    
    
a.callToAction span {
    color: #434343;    
}
    
a.callToAction span:hover {
    color: #D6D6D6;    
}    
    
    
    
/************ Misc ***********/
    
    
/* Spoiler button */
    
.button {
    color: #BABABA;
    background-color: #434343;
    border: 1px solid #525252;
}    
    
.button:hover {
    color: #BABABA;
    background-color: #525252;
    border: 1px solid #525252;
}     
    
/* spoiler */
    
.hasJs .bbCodeSpoilerText {
    background-color: #434343;
    border: 1px solid #525252;    
}    
    
/* Tables */
    
.dataTable {
    background: #434343;
    border: 1px solid #525252;
}    
    
.dataTable tr {
    color: #D6D6D6;
    text-shadow: 0 1px 1px rgba(256, 256, 256, 0.1);
}    
    
.dataTable tr:nth-child(2n+1) td {
    background: #484848;
}    
    
.dataTable td {
    border-right: 1px solid #525252;
    border-top: 1px solid #525252;
    border-left: none;
    border-bottom: none;
}    
    
.dataTable th {
    color: #434343;
}
    
/* Search */
    
.searchResult .snippet a {
    color: #D6D6D6;
}    
    
    
.agendaSelect .textCtrl, .xenForm .ctrlUnit > dd .textCtrl {
    background-color: #434343 !important;    
}   
    
#QuickSearch.show {
    box-shadow: 4px 4px 10px -2px rgba(0,0,0,.1);
    border: solid 1px #525252;
    background: #434343;
    color: #D6D6D6;
}    
    
#QuickSearch .formPopup .secondaryControls {
    color: #D6D6D6;
}    
    
#calroot {
    background-color: #434343;    
}  
    
.calweek a:hover {
background-color: #525252;
}    
    
#caltitle {
    color: #D6D6D6;
}    
    
/* Options page */
    
.heading, .xenForm .formHeader {
    font-weight: bold;
    font-size: 16px;
    color: #BABABA;
    background-color: #484848;
    padding: 20px 15px;
    border-top: 2px solid #eeaa21;
}    
    
.navigationSideBar a {
    padding: 12px 15px;
    border-bottom: solid 1px #525252;
    background-color: #434343;
}  
    
.navigationSideBar a:hover {
    background-color: #525252;
}      
    
.navigationSideBar .secondaryContent {
    color: #D6D6D6;
    background-color: #484848;
}    
    
/* Signature gradient */
    
.uix_signatureCover {
    display: none;
    opacity: 0;
    -webkit-transition: opacity 0.3s ease-out;
    transition: opacity 0.3s ease-out;
    box-sizing: border-box;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding-top: 53.333333333333px;
    text-align: center;
    background: linear-gradient(to bottom, rgba(61, 61, 61, 0) 0%, rgb(61, 61, 61) 80%);
}    
    
.threadListSeparator {
    color: #D6D6D6;
    background-color: #434343;  
    border: 0px solid #383838;
    border-top-width: 1px;
    border-bottom-width: 1px;
}    
    
    
/* Thread tags */
    
.tagList .tag {
    color: #D6D6D6;
    background-color: #434343;
    border: 1px solid #525252;
        border-left-style: solid;
    border-left-style: none;
    border-top-left-radius: 0;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 0;
}    
    
.tagList .tag:hover {
    color: #D6D6D6;
    background-color: #525252;
    border: 1px solid #525252;
        border-left-style: solid;
    border-left-style: none;
    border-top-left-radius: 0;
    border-top-right-radius: 4px;
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 0;
}     
    
.tagList .tag .arrow {
    border-style: solid;
    border-width: 8px 9px 8px 0;
    border-width: 11px 9px 11px 0;
    border-color: transparent;
        border-right-color: transparent;
    border-right-color: #434343;
    border-right-color: #434343;
}    
    
.tagList .tag .arrow::after {
    border-style: solid;
    border-width: 7px 8px 7px 0;
    border-width: 10px 8px 10px;
    border-color: transparent;
        border-right-color: transparent;
    border-right-color: #525252;
    border-right-color: #525252;
}    
         
/* KMS Tag on main page */
    
.prefix.prefixPrimary {
    color: #B6B6B6;
    background-color: #A6329E;
    border-color: #A6329E;
}    
    
/* Alert */
    
.navPopup .listItem:hover {
    background: #525252;
}    
    
    
    
    
    
    
}
