css:
@-moz-document domain("sj.uukanshu.net") {
.txt_tj_book > ul,
.samebooks > ul,
.samebooks1 > ul,
ul#chapterList {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
}
.txt_tj_book > ul > li,
.samebooks > ul > li,
.samebooks1 > ul > li,
ul#chapterList > li {
  display: block;
  flex-grow: 1;
  width: 30%;
}
.samebooks > ul > li > a,
.samebooks1 > ul > li > a {
  display: block;
  width: 100%;
  white-space: pre;
  text-overflow: ellipsis;
  overflow: hidden;
}
ul#chapterList > li {
  height: auto !important;
  line-height: 1 !important;
  width: 40% !important;
  flex-grow: 1;
  padding: 0 !important;
  margin: 0 !important;
  position: relative;
}
@media (min-width: 33rem) {
  .txt_tj_book > ul > li,
  .samebooks > ul > li,
  .samebooks1 > ul > li,
  ul#chapterList > li {
    width: 30% !important;
  }
}
@media (min-width: 44rem) {
  .txt_tj_book > ul > li,
  .samebooks > ul > li,
  .samebooks1 > ul > li,
  ul#chapterList > li {
    width: 25% !important;
  }
}
@media (min-width: 55rem) {
  .txt_tj_book > ul > li,
  .samebooks > ul > li,
  .samebooks1 > ul > li,
  ul#chapterList > li {
    width: 20% !important;
  }
}
ul#chapterList > li > a {
  display: block;
  width: 100%;
  white-space: pre;
  text-overflow: ellipsis;
  overflow: hidden;
  padding: 1px 0.25rem;
}
.share {
  display: none !important;
}
.search {
  border-width: 1px;
}
#read-page .rp-switch {
  padding: 1rem 0;
}
.rp-switch + .samebooks1,
.ad_content,
.helpbox,
.ggad {
  display: none !important;
}

/*[[touch-menu]]*/

}
