css:
@-moz-document domain("kinokrad.co") {
.warning-adblock {
    display: none!important;
}
#wrapper #middle,
#wrapper.bgmain #header,
#contbox,
.soondate,
.commtext-box,
.headermenu {
    background-color: #c1c1c1;
}
.comedit .arg a,
.comedit .argreply a {
    color: #bd0000;
}
.search {
    background-color: #ad3;
}
.quote,
.scriptcode,
.text_spoiler,
.title_quote,
.title_spoiler {
    background: #85c5c5;
}
.title_quote {
    background-color: #3d63a7;
    color: #fff;
}
.commtext {
    background-color: #797ca0;
}
.godshort .fader2,
.janrshort .fader2 {
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 1) 96%);
    background: -o-linear-gradient(left, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 1) 96%);
    background: -ms-linear-gradient(left, rgba(255, 255, 255, 0) 0, rgba(255, 255, 255, 1) 96%);
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0, rgb(193, 193, 193) 96%);
}
.leftbox-comm {
    background-color: #434343;
    color: #fff;
}
.autitl .fader {
    background: -o-linear-gradient(left, rgba(242, 242, 242, 0) 0, rgba(242, 242, 242, .99) 99%, rgba(242, 242, 242, 1) 100%);
    background: -ms-linear-gradient(left, rgba(242, 242, 242, 0) 0, rgba(242, 242, 242, .99) 99%, rgba(242, 242, 242, 1) 100%);
    background: linear-gradient(to right, rgb(67, 67, 67) 0, rgb(67, 67, 67) 99%, rgb(67, 67, 67) 100%);
}
#sideRight .soondate a {
    color: #000!important;
}
textarea#comments {
    border: 1px solid #0f4a67;
    background-color: #bbc;
}
}

@-moz-document url("http://kinokrad.co/views/"), url("http://kinokrad.co/comments/"), url("http://kinokrad.co/rating/") {
table.tabltop {
    background-color: #29c143;
    color: #000000;
    border: 1px solid #0f4a67;
}
table.tabltop tbody .alt td {
    background: #0480be;
    color: #000000;
    border: 1px solid #0f4a67;
}
table.tabltop tbody td {
    color: #000000;
}
}

@-moz-document url-prefix("http://kinokrad.co/favorites/") {
#sliderblock,
#sliderbox {
    display: none!important;
}
#wrapper #middle {
    padding-top: 50px!important;
    position: relative!important;
    top: -200px!important;
    left: -140px!important;
    width: 125%!important;
}
#contbox {
    position: relative!important;
    width: 97%!important;
}
.shorposterbox {
    position: relative!important;
    width: 138%!important;
}
.headermenu {
    position: relative!important;
    left: 100px !important;
}
div.shortboxh {
    width: 76%!important;
}
.shorttext {
    width: 650px;
}
div.godshort {
    line-height: 20px;
    font-weight: normal;
}
div.shorttext {
    font-size: 16px;
    line-height: 20px;
}
}
