css:
@-moz-document domain("keep.google.com") {

/* Note font: */
.IZ65Hb-YPqjbf {
    font-family: 'Liberation sans', Arial, Tahoma, sans-serif !important;
    color: #111;
}

/* Note body: */
div[style="width: 632px;"] {
   width: 90% !important;
}
div[style*="width: 600px"] {
   width: 90% !important;
}
div.ma6Yeb-r8s4j-gkA7Yd, div.IZ65Hb-n0tgWb.RNfche {
   margin-left: auto !important;
   margin-right: auto !important;
}
.IZ65Hb-YPqjbf {
   background-color: #FFF;
}

/* Update note layer: */
div[style*="left: 478px; top: 30px"] {
   left: 10% !important;
   right: 10% !important;
   top: 30px !important;
}

/* Note header: */
.notranslate.IZ65Hb-YPqjbf.r4nke-YPqjbf {
	font-weight: bold;
	font-size: 120%;
	border-bottom: none;
	padding-bottom: 8px !important;
}
  
/* Note footer: */
.IZ65Hb-yePe5c {
    border-top: 1px solid #DDD;
}

/* Sidebar: */
.gb_oc,
.gb_pc {
	font-weight: bold;
}

.gb_ic.gb_jc {
	background-color: #fff;
	-webkit-box-shadow: 1px 0px 2px -1px #888;
	box-shadow: 1px 0px 2px -1px #888;
}

.gb_pc, .gb_qc {
    min-height: 26px !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

}
