@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("greasespot.net") {
  /*[[theme]]*/
  
  ::-moz-selection {
    color: var(--bg) !important;
    background: var(--fg-link) !important;
  }
  ::selection {
    color: var(--bg) !important;
    background: var(--fg-link) !important;
  }
  
    body, #p-cactions li a, .infobox th, #header-wrapper, #header-wrapper #header
  { background-color: var(--bg) }
    #header, .navbar, #footer, #footer .business_card, .popover, .popover-title, .popover-content, .dropdown-menu, .mw-body, .pBody, #p-cactions li.selected a, div#footer, #main-wrapper,#content-wrapper, #sidebar-wrapper
  { background-color: var(--bg-2) }
    .catlinks, code, #pagehistory li.selected, .infobox, #toc, .toc, .sidebar h2, table.wikitable
  { background-color: var(--bg-odd) }
    input, input[type="text"], input[type="search"], input[type="number"], select, textarea, .form-control, .mw-ui-input
  { background-color: var(--bg-input); color: var(--fg-input) }
    #wpNotes
  { background-color: var(--bg-input) !important; color: var(--fg-input) !important }
    input:focus, input[type="text"]:focus, input[type="search"]:focus, select:focus, textarea:focus, .form-control:focus, .mw-ui-input:focus, input[type="number"]:focus
  { background-color: var(--bg-input-hover); color: var(--fg-input-hover) }
    #p-cactions li:not(.selected) a:hover, input[type="submit"]
  { background-color: var(--fg-link); color: var(--bg) }
    td.diff-deletedline .diffchange
  { background-color: rgba(255,0,0,0.4) }
    td.diff-addedline .diffchange
  { background-color: rgba(0,255,0,0.4) }
    td.diff-context, hr
  { background-color: rgba(255,255,255,0.2) }
    #p-personal li a:hover
  { background-color: transparent }
    body, .navbar, input[type="text"], select, textarea, .form-control
  { background-image: none }

    body, h3, h4, h5, #p-cactions li.selected a, .mw-body, td.diff-context, .infobox th, .tocnumber, .infobox, .sidebar, table.wikitable
  { color: var(--fg) }
    h1, h2, code, .sidebar h2, .post-title a, .post-title a:link, .post-title a:visited
  { color: var(--fg-2) }
    .muted, #contentSub, .autocomment, .date-header
  { color: var(--fg-muted) }
    a, a:link, #p-cactions li a, .mw-body-content a.external, #p-personal li a, #p-personal li a:visited, .mw-body-content a.extiw
  { color: var(--fg-link) }
    a:hover, a:visited:hover, .mw-body-content a.external:hover, #p-personal li a:hover, .mw-body-content a.extiw:hover, .post-title a:hover
  { color: var(--fg-link-hover) }
    a:visited, #p-cactions li a:visited, .mw-body-content a.external:visited
  { color: var(--fg-link-visited) }
    #p-cactions .new a, .mw-plusminus-neg, a.new:link, a.new:visited
  { color: #f33 }
    .mw-plusminus-pos
  { color: #3f3 }

    a
  { text-decoration-color: var(--fg-link) }
  
    noborder, .navbar, .mw-body, .pBody, #p-cactions li, .catlinks, div#footer, div.noarticletext, .infobox, #toc, .toc, #content-wrapper
  { border: none }
    input[type="search"], input[type="text"], input[type="submit"], input[type="number"], input, select, textarea
  { border: var(--border) }
    h1, h2, h3, .mw-ui-input, fieldset
  { border-color: var(--border-color) }
    pre, code, #pagehistory li, table.wikitable > * > tr > td
  { border-color: rgba(255,255,255,0.2) }
    pre.sample-good, td.diff-addedline
  { border-color: #3f3 }
    pre.sample, td.diff-context
  { border-color: var(--fg-muted) }
    pre.sample-bad, td.diff-deletedline
  { border-color: #f33 }

    success, success a
  { background-color: var(--bg-success); color: var(--fg-success) }
    info, info a
  { background-color: var(--bg-info);    color: var(--fg-info) }
    warning, warning a
  { background-color: var(--bg-warning); color: var(--fg-warning) }
    error, error a, div.noarticletext
  { background-color: var(--bg-error);   color: var(--fg-error) }
    disabled
  { background-color: var(--bg-disabled);   color: var(--fg-disabled) }

    pre
  { background-color: var(--syntax-bg); border: none }
    pre
  { color: var(--syntax-fg) }

    .mw-icon-arrow-collapsed, .mw-collapsible-arrow.mw-collapsible-toggle-collapsed
  { filter: invert(100%) }
}

