css:
@-moz-document domain("www.facebook.com") {
/* -----
	FRIENDS CHAT SIDEBAR
----- */

.fbChatSidebar {
	border-left: 0;
	box-shadow: 2px 0px 10px 4px #d0d0d0 !important;
}
/*.uiScrollableAreaBody {
    	width: 80px !important;   
    }*/

div.fbChatSidebar.fixed_always {
	/* when sidebar chat opened */
	
	width: 80px;
	padding: 0px;
}
li._42fz {
	/* sidebar chat list friends ul lines */
	
	margin: 6px 0px;
	width: 80px !important;
}
._55lr {
	/* nome dos amigos no chat lateral */
	
	display: none;
}
._55lp {
	/* divisor que agrupa thumbnail e bolinha online*/
	
	margin-left: 20%;
}
._55oc {
	/* divisor "AMIGOS" e "MAIS AMIGOS (nº online) */
	
	width: 80px;
}
._5bon {
	/* div bolinha verde */
	
	float: none;
	position: absolute;
	top: 16px;
	left: 22px;
}
._568z span {
	/* bolinha verde em si */
	
	height: 10px !important;
	width: 10px !important;
	border: 2px solid #e9ebee;
}
/* -----
	MAIN AREA
----- */

.sidebarMode #globalContainer {
	padding-right: 12px;
}
/* main screen left nav section */

div#pagelet_navigation li {
	margin: 6px 0;
}
li.sideNavItem.selectedItem {
	box-shadow: 2px 2px 5px 1px #e0e0e0;
}
._4-u2._1-ib._2tyk._20os._4-u8 {
	background: #e9ebee;
	border: 0px;
	box-shadow: -1px 1px 7px 1px #d4d4d4;
}
div#pagelet_ego_pane {
	display: none !important;
}
._2yq._2ltu ._4-u2:before {
	border: 0px solid black;
}
._2yq ._4-u2::before {
	border: 0px solid black;
}
/* -----
	TOP NAVIGATION BAR
----- */
/* top blue bar */

._2s1x ._2s1y {
	border-bottom: none;
	box-shadow: #8a98b5 0px 0px 5px 2px;
}
/* buttons */

._2s1x ._2s25,
._2s1x ._2s25,
._2s1x ._2s25 {
	color: inherit;
	border-radius: 5px;
	margin-right: 6px;
	margin-left: 6px;
}
}
