css:
@-moz-document url-prefix("https://imgur.com/a/"), url-prefix("https://imgur.com/gallery/") {
/*DeepDark colors*/
:root{
		--main-color: #00adee;
		--main-background: #111111;
		--second-background: #181818;
		--hover-background: #232323;
		--main-text: #eff0f1;
		--dimer-text: #CCCCCC;
}

#topbar, .navNext, .navPrev, #right-content, .post-action, #div-gpt-ad-spotlight, .div-gpt-ad-active-top_banner, #promoted-spotlight, #captions, .root-caption-container, .comment-container, #recommendations,
#top-comments, .icon-mobile, .post-gridview, .post .point-info, .sentence-sorting{
    display: none;
}
.post .hover{
    display: none !important;
}
.post, .post:hover{
    border: 0;
    margin: 6px 4px;
    background-color: var(--second-background);
}
body, html{
    background-color: var(--main-background);
    color: var(--main-color);
}
.post-account, .post-account:active, .post-account:hover, .post-account:visited, .post-title-meta, a, a:hover, a.post-loadall:hover, a.post-loadall, .post-image-description, .post-description{
	color: var(--main-text);
}
a, a:hover{
    font-weight: bold;
}
.left{
    float: none;
}
.post-container{
    margin: 0 auto;
}
.post-title-container{
    max-width: 100%;
}
.post-title{
    text-align: center;
    font-size: 1.5em;
    color: var(--main-color);
}
.post-title-meta{
    text-align: center;
    font-size: .9em;
}

.post-header.fixed{
    position: relative;
    z-index: auto;
    top: auto;
    border-radius: 0;
    display: block !important;
}
.post-header{
    position: relative;
    border-radius: 0;
    min-height: 48px !important;
    min-width: 688px !important;
    background-color: var(--hover-background);
}
.post-container{
    padding-top: 0 !important;
}
.post-grid-image, .post-images, .post-image, .post-image-container--spacer, .posts{
    background-color: var(--second-background);
}
.js-post-truncated, .post-image-meta, .post-image-meta.post-image-meta--last{
    padding: 20px;
    background-color: var(--hover-background);
}
a.post-loadall:hover, a.post-loadall{
    background-color: var(--main-color);
}
.posts{
    border-radius: 0;
}
.left.post-pad{
    margin: 0;
}
*{
    box-shadow: none !important;
}
#inside{
    width: auto;
}
}
