css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aywas.com") {
  html, body {
    font-size: 12px;
    font-family: Arial, Helvetica, sans-serif;
    color: #cccccc;
  }
  
  div#backdrop {
      background-color: #121212;
  }
  
  a:link, a:visited {
    color: #6492c4;
  }
  
  table.y-tables{
    border: 1px solid #2c3d47;
  }
  table.y-tables tr th.highlight,
  table.y-tables tr.highlight th{
    background-color: #2c3d47;
    color: #FFF;
  }
  table.y-tables tr.row-2,
  table.y-tables tr.row-even,
  table.y-tables tr.highlight,
  table.y-tables tr td.highlight,
  table.css3 tr:nth-child(odd) td{
    background-color: #2c3d47;
  }
  table.y-tables tr.flagged {
    background-color: #373d37;
  }
  table tr.category-title th {
    background-color: #344956;
  }
  div#board-listing table tr.sticky {
    background-color: #6d503b;
  }

  input[type="text"], textarea {
      padding: 2px;
      border-top: 1px solid #2c3d47;
      border-left: 1px solid #2c3d47;
      border-bottom: 1px solid #2c3d47;
      border-right: 1px solid #2c3d47;
      color: #cccccc;
      background-color: #121212;
  }
  input.a-button[type="button"], a.a-button, input.a-button[type="submit"] {
    border: solid 1px #2c3d47;
    color: #cccccc;
    background-color: #373d37;
    text-shadow: 0px 1px 1px #2c3d47;
  }
  
  /* Does not work because page nav bg is an image.*/
  /*
  #page-nav li {
    border-right: 1px solid #2c3d47;
  }
  */
}
