css:
/* Written by electricjonny.  Not everything is commented, and it's a bit jumbled up, but oh well.

Feel free to modify this or take code out to use in whatever you want :) */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.deviantart.com") {

/* ----------- The tops of the main page ---------- */
body.deviant #output div.bubbleview div.catbar {
background:#d3dfd1 url(data:image/gif;base64,) repeat-x!important;
border-top:1px solid #a6b2a6!important;
border-bottom:1px solid #a6b2a6!important;
border-left:0px solid #a6b2a6!important;
border-right:0px solid #a6b2a6!important;
margin:-7px -15px 12px -15px!important;
padding:5px 0px 15px 10px!important;
-moz-border-radius:4px 4px 0px 0px!important;
}

/* ------------ The Categories button ------------ */
div.bubbleview div.catpath a.catbutton, div.bubbleview div.catpath #catbutton {
background:url(data:image/gif;base64,) no-repeat!important;
height:27px!important;
margin-top:3px!important;
}

div.bubbleview div.catpath a.catbutton:hover, div.bubbleview div.catpath #catbutton:hover {
background:url(data:image/gif;base64,) no-repeat!important;
height:27px!important;
margin-top:3px!important;
}

div.bubbleview div.catpath a.catbutton:active, div.bubbleview div.catpath #catbutton:active {
background:url(data:image/gif;base64,) no-repeat!important;
height:27px!important;
margin-top:3px!important;
}

body.deviant div.bubbleview {
background:#ccd9c8!important;
border-left:1px solid #a6b2a6!important;
border-right:1px solid #a6b2a6!important;
}

body.deviant table.fora td.f {
background:#dae4d9!important;
-moz-border-radius:0px!important;
border:1px solid #a6b2a6!important;
border-collapse:separate!important;
}

table.f td.f, table.f th.f {
border-collapse:separate!important;
border:1px solid #a6b2a6!important;
margin:0px!important;
}

body.deviant table.fora th strong, body.deviant table.forum th strong {
border-bottom:0px solid #a6b2a6!important;
padding-left:5px!important;
vertical-align:middle!important;
background:#e6ede4!important;
font-size:10pt!important;
color:#40534a!important;
}

/* ----------- This makes the left and right of pages look right ----------- */
#output {
background:url(data:image/gif;base64,R0lGODlhAQC2CaIAAE9jV05jV05iWE5iV3KHdgAAAAAAAAAAACH5BAAAAAAALAAAAAABALYJAANBGCDT/jDKSattJOvNu/9gKI5kaZ5oqq5s675wLM90bd94ru987//AoHBILBqPyKRyyWw6n9CodEqtWq/YrHbLTQAAOw==)repeat-x!important;
}

/* ----------- The link colors ----------- */
body.deviant div.bubbleview a.a {
color:#40534a!important;
}

body #output a.h, body a.h {
color:#40534a!important;
}

body #output a.h:visited, body a.h:visited {
color:#97a194!important;
}

/* ----------- Makes the Status forum text bigger (read it people) ------------ */
/*body.deviant div.bubbleview a.a[href="http://forum.deviantart.com/devart/status/"] {
font-size:11pt!important;
}

body #output a.h[href^="/devart/status/"], body a.h[href^="/devart/status/"] {
font-size:13pt!important;
}*/

/* ---------- Comments ----------- */
div.thought {
padding:2px 2px 2px 2px!important;
}

div.nest {
padding-left:15px!important;
}

div.thought {
min-height:10px!important;
}

#forum #comments .thought, #forum .comments .thought {
border: 6px solid #a6b2a6 !important;
margin-top:6px!important;
-moz-border-radius: 5px !important;
-moz-border-bottom-colors: #a6b2a6 #d3ded3!important;
-moz-border-top-colors: #a6b2a6 #d3ded3!important;
-moz-border-left-colors: #a6b2a6 #d3ded3 !important;
-moz-border-right-colors: #a6b2a6 #d3ded3!important;
padding: 0px !important;
background-color: #d3ded3!important;
overflow: hidden !important;
/* ---------- Arrow to the right of your icon ---------- */
/*background-image: url(data:image/gif;base64,R0lGODlhDQANAJECAKaypuTp4////wAAACH5BAEAAAIALAAAAAANAA0AAAIihI8nEO0PxHo0SkabvTlsXkniBI0ippmn85lY63qqC6tAAQA7) !important;
background-repeat: no-repeat !important;
background-position: 59px 12px !important;*/
}

#forum #comments .thought .author, #forum .comments .thought .author {
background: transparent !important;
margin-left: 70px !important;
padding: 4px !important;
display: inline-block !important;
padding-bottom: 0px !important;
}

#forum #comments .thought .author .avatar, #forum .comments .thought .author .avatar {
margin-left: 65px !important;
margin-top:-6px!important;
}

#forum #comments .thought .body, #forum .comments .thought .body {
margin: 0px !important;
margin-left: 70px !important;
padding: 10px !important;
background-color: #e4e9e3 !important;
min-height: 45px !important;
-moz-border-radius: 5px !important;
border: 1px solid #a6b2a6 !important;
border-left-color: #a6b2a6 !important;
border-top-width: 1px !important;
border-top-color: #a6b2a6 !important;
margin-top: -28px !important;
padding-top: 40px !important;
}

#forum #comments .thought .links, #forum .comments .thought .links {
margin-left: 70px !important;
}

/* ---------- Replybox ---------- */
textarea, [style="font-size: 8.25pt;"], .dpToolbar  {
margin-left:75px!important;
}
   
/* ---------- Reply button ---------- */
a.replybutton {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

a.replybutton:hover {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

a.replybutton:active {
background:url(data:image/gif;base64,) no-repeat!important;
width:50px!important;
height:27px!important;
margin-left:-6px!important;
margin-bottom:-8px!important;
}

/* ---------- Locked ---------- */

[style="background: rgb(186, 197, 186) none repeat scroll 0% 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"] {
background:#d3ded3!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
}

/* ---------- Mood Box ---------- */

[class="f moods"] {
background:#d3ded3!important;
}

body.deviant table.moods td.active {
background:#728776!important;
}

/* ---------- Forum Pages ---------- */

body.deviant table.forum tr.sticky td, body.deviant table.forum td.sticky a {
/* ---------- Big red font ---------- */
/*font-size:11pt!important;
color:#862828!important;*/
background:#e6ede4!important;
}

[colspan="4"] {
display:none!important;
}

body.deviant table.fora td.f, body.deviant table.forum td.f {
background:#dae4d9!important;
}

table.forum th.f {
background:#53695c!important;
padding-bottom:0px!important;
}

[cols="60"] {
margin-top:24px!important;
margin-left:0px!important;
margin-bottom:-27px!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
background-color:#e3ebe9!important;
}

[cols="60"]:focus {
margin-top:24px!important;
margin-left:0px!important;
margin-bottom:-27px!important;
border:1px solid #a6b2a6!important;
-moz-border-radius:5px!important;
background-color:#f0fbf8!important;
}

div.deviousthought div.moodarea div.preview, div.deviousthought div.moodarea div.preview {
position:relative!important;
left:-70px!important;
top:27px!important;
width:98%!important;
height:162px!important;
margin-bottom:-22px!important;
}

div.deviousthought div.controls {
margin-top:24px!important;
}

[class="trailing section-foot c"] {
margin-top:10px!important;
}

#previewdiv {
margin-top:24px!important;
}

#commentsubject {
width:510px!important;
}

.dpToolbar {
margin-top:26px!important;
margin-left:0px!important;
margin-bottom:-25px!important;
}

/* ---------- Hide Comment Moods ---------- */

div.deviousthought div.moodarea td.t2 div.browser, div.deviousthought div.moodarea td.t2, div.deviousthought div.moodarea th {
display:none!important;
}

/* ----------- Next/Previous ---------- */
.nav span {
border:0px!important;
}

div.bubbleview div.comments div.nav a {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
}

div.bubbleview div.comments div.nav a:hover {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAObr5ubq5evu6ens5+ru6ebq5OPn4efs5unt6evv6vr7+ejs5+Hm4OXp5Ons6Ozv7OPo4uLn4OTp4+zv6wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoPI805QIBOIMywEERSNJEGREERMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
}

div.bubbleview div.comments div.nav a:active {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAM3WyszVydHYztje19PZ0dTb0+Dk4ODk3+/w7tXb1Nre2cnTyN/i3N3i3Nbc1d7i3c7Xzc/Wy9zg2dzh3N3g2tfd1t/j3gAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUV4BIAESQQReJU1aBI1NQwj2UdBhICADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
}

/* ----------- The buttons ---------- */
[type="button"], [type="submit"]:not([value=""]) {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAN7k3eLn4d3j2+Hm397j3Nrg2OLm4Nvi2t/l3uPo4tzi2+Dl39fe1dvh2eTp49ng2OTo4tnf1/j599jf1uXp5Obq5QAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoFRVFOVASWAMCwIQgnI0xRNNExMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

[type="button"]:hover, [type="submit"]:not([value=""]):hover {
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAObr5ubq5evu6ens5+ru6ebq5OPn4efs5unt6evv6vr7+ejs5+Hm4OXp5Ons6Ozv7OPo4uLn4OTp4+zv6wAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUVoPI805QIBOIMywEERSNJEGREERMCADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

[type="button"]:active, [type="submit"]:not([value=""]):active{
-moz-appearance:none!important;
background:transparent url(data:image/gif;base64,R0lGODlhAQAZAMQAAM3WyszVydHYztje19PZ0dTb0+Dk4ODk3+/w7tXb1Nre2cnTyN/i3N3i3Nbc1d7i3c7Xzc/Wy9zg2dzh3N3g2tfd1t/j3gAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABABkAAAUV4BIAESQQReJU1aBI1NQwj2UdBhICADs=) repeat-x!important;
border:1px solid #939f93!important;
-moz-border-radius:6px!important;
color:#2c3635!important;
width:auto!important;
height:27px!important;
}

/* ----------- Gets rid of the dotted outline on links when clicked ----------- */
a {
outline:none!important;
}

/* ----------- Gets rid of the space at the bottom of the main page ----------- */
table.fora {
margin-bottom:8px !important;
}
}
