css:
@-moz-document domain("store.steampowered.com"){
body,h1,h2,div,td,th,a,ul,li{
font-family: "Microsoft Yahei", Sans-serif!important;
}
span.bb_spoiler:hover{
	color:#596f86;
    background-color:#171a21;
}
.review_box .content{
	font-size: 16px;
    line-height: 25px;
    color: #5e7184;
}
.review_box .shortcol .content{
	color: #7690ab;
}
div.early_access_announcements .post{
	font-size: 14px;
    line-height: 20px;
    color: #5e7184;
}
div.early_access_announcements .post h1{
    font-size: 20px;
    line-height: 25px;
}
div.early_access_announcements .post h1 a,
div.early_access_announcements .post .more a{
	color: #a1aab3;
}
.game_description_column{
	color: #5e7184;
}
.game_area_description ul{
    color: #7a838c;
}
}
@-moz-document domain("steamcommunity.com"){
body,h1,h2,div,td,th,a,ul,li{
	font-family: "Microsoft Yahei", Sans-serif!important;
}
a{
	color: #90aed4;
}
.apphub_UserReviewCardContent .apphub_CardTextContent{
	font-size: 16px;
    line-height: 25px;
    color: #5e7184;
}
.apphub_UserReviewCardContent .found_helpful{
    color: #5e7184;
}
.apphub_UserReviewCardContent .vote_header .title,
.apphub_UserReviewCardContent .vote_header .hours,
.ratingSummary,
.playTime{
    color: #a1aab3;
}
.review_area_content,
.review_developer_response_text,
.commentthread_comment_text,
.apphub_CardContentNewsDesc{
	font-size: 16px;
    line-height: 25px;
    color: #727e8a;
}
.btn_grey_grey{
	color: #acb2b8 !important;
}
.general_btn.panel_btn{
	color: #acb2b8;
}
.general_btn.panel_btn:hover{
	color: #90aed4;
}
.apphub_CardContentNewsTitle{
    color: #a1aab3;
}
}
